import IntlTelInput, { CountryData } from "react-intl-tel-input";
import { useTranslation } from "react-i18next";
import styled from "styled-components";
import parsePhoneNumber from "libphonenumber-js";

import { StyledError } from "@components";
import { FormikErrors } from "formik";
import { IEmailForm, IPhone, IUserSetting } from "@interfaces";
import { useEffect, useState } from "react";

interface IProps {
    setFieldTouched: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IUserSetting | IEmailForm | IPhone>>;
    setFieldValue: any;
    value: string;
    errorPhone: string;
    setError: React.Dispatch<React.SetStateAction<string | undefined>>;
    disabled?: boolean | undefined;
    country: string | undefined;
    handleChangePhoneNumber: (
        isValid: boolean,
        value: string,
        selectedCountryData: CountryData,
        fullNumber: string
    ) => void;
    handleSelectFlag: (
        currentNumber: string,
        selectedCountryData: CountryData,
        fullNumber: string
    ) => void;
}

export const SharedIntlTelInput = (props: IProps) => {
    //page hooks
    const { t } = useTranslation();
    //page props
    const { setFieldTouched, value, errorPhone, setFieldValue, setError, disabled, country } =
        props;
    const handleSelectFlag = (
        currentNumber: string,
        selectedCountryData: CountryData,
        fullNumber: string
    ) => {
        setFieldValue("phoneNumber", currentNumber.replace(/(\s|-)/g, ""));
        setFieldValue("phone", fullNumber.replace(/(\s|-)/g, ""));
    };

    const handleChangePhoneNumber = (
        isValid: boolean,
        value: string,
        selectedCountryData: CountryData,
        fullNumber: string
    ) => {
        if (!isValid) setError(t("validation.isNumber"));
        else {
            setError("");
        }

        setFieldValue("phoneNumber", value);
        setFieldValue("phone", fullNumber.replace(/(\s|-)/g, ""));
    };

    return (
        <>
            <StyledIntlTelInput
                defaultCountry={country || "th"}
                preferredCountries={["th", "vn"]}
                fieldId="phoneNumber"
                fieldName="phoneNumber"
                onPhoneNumberChange={handleChangePhoneNumber}
                onPhoneNumberBlur={() => {
                    setFieldTouched(value || "", true);
                }}
                onSelectFlag={handleSelectFlag}
                value={value}
                disabled={disabled}
                autoFocus={true}
                formatOnInit={false}
            />
            {errorPhone && (
                <StyledError className="custom-error" visible={true}>
                    {errorPhone}
                </StyledError>
            )}
        </>
    );
};

const StyledIntlTelInput = styled(IntlTelInput)`
    input.allow-dropdown input[type="tel"] {
        border-radius: 6px;
    }
`;
