import styled from "styled-components";
import { IMembershipTier } from "@interfaces";
import { IConClose } from "@components";
import { useTranslation } from "react-i18next";

interface IProps {
    membershipTier: IMembershipTier;
}

export const ComponentCurrentPoint = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const { membershipTier } = props;

    console.log(membershipTier);

    return (
        <StyledCurrentPoint>
            <div className="content_currentPoint">
                <div className="close">
                    <IConClose />
                </div>
                <div className="title">
                    <img src={membershipTier.iconUrl} width={26} />
                    <span>
                        {membershipTier.membershipName} {t("page.member_shipTier.member")}
                    </span>
                </div>
                <div className="baht_spent">
                    {membershipTier.bahtSpent}à¸¿ {t("page.member_shipTier.spent")} ={" "}
                    {membershipTier.points} {t("page.member_shipTier.point")}
                </div>
                {membershipTier?.benefits ||
                    (membershipTier?.benefits.length !== 0 && <div className="benefits"></div>)}
            </div>
        </StyledCurrentPoint>
    );
};

const StyledCurrentPoint = styled.div`
    height: 100vh;
    width: 100%;
    position: fixed;
    top: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: 480px;
    background: rgba(0, 0, 0, 0.5);
    .content_currentPoint {
        width: 90%;
        padding: 28px;
        background-color: #fff;
        position: relative;
        border-radius: 8px;
        max-height: 90%;
        overflow: scroll;
    }
    .close {
        position: absolute;
        top: 20px;
        right: 20px;
        cursor: pointer;
    }
    .title {
        img {
            margin-right: 8px;
        }
        span {
            font-family: "Roboto", sans-serif;
            font-style: normal;
            font-weight: 600;
            font-size: 20px;
            color: #000000;
            position: relative;
            top: 7px;
        }
    }
    .baht_spent {
        font-family: "Roboto", sans-serif;
        font-style: normal;
        font-weight: normal;
        font-size: 18px;
        color: #7e7e7e;
        margin: 8px 0 16px 0;
    }
`;
