import { useSelector } from "react-redux";
import styled from "styled-components";
import { useTranslation } from "react-i18next";

import { IExternalAuthState } from "@interfaces";
import { SharedSocialLine, StyledSignUp } from "..";
import { ModuleSocialMedia } from "@modules";
import { enumExternalAuthType } from "@configs";
import { selectMerchant } from "@redux";

export const ComponentSocial = () => {
    //page hook
    const { t } = useTranslation();
    // redux state
    const merchant = useSelector(selectMerchant);

    const state: IExternalAuthState = {
        type: enumExternalAuthType.SIGNUP,
    };
    return (
        <StyledSignUp>
            <StyledContainer>
                <div className="title">
                    {t("page.auth.sign_up_to")} {merchant.name}
                </div>
                <div className="choose">{t("page.auth.choose_sign_up_method")}</div>
            </StyledContainer>
            <ModuleSocialMedia state={state} />
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
`;
