import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import { useHistory } from "react-router";

import { ComponentHeaderAuth, StyledContainerSignIn } from "@components";
import { selectAuth, selectMerchant } from "@redux";
import { enumExternalMethod, PATH_SIGN_UP, PATH_SIGN_UP_SOCIAL } from "@configs";
import { ModuleTelSignIn } from "@modules";
import { PageUserDetail } from "@pages";

export const PageSignInTel = () => {
    //page hook
    const { t } = useTranslation();
    const history = useHistory();
    //redux state
    const merchant = useSelector(selectMerchant);
    const { detailUser } = useSelector(selectAuth);

    const handleSignUp = () => {
        if (merchant?.signUpSettings?.signUpMethods.includes(enumExternalMethod.EMAIL)) {
            history.push(PATH_SIGN_UP);
        } else if (merchant?.signUpSettings?.signUpMethods.includes(enumExternalMethod.TEL)) {
            history.push(PATH_SIGN_UP);
        } else {
            history.push(PATH_SIGN_UP_SOCIAL);
        }
    };

    return (
        <>
            {!detailUser ? (
                <StyledContainerSignIn color={merchant.themeColor}>
                    <ComponentHeaderAuth />
                    <ModuleTelSignIn />
                    <div className="sign_in">
                        {t("page.auth.already_have_an_account")}{" "}
                        <span onClick={handleSignUp}>{t("page.auth.sign_up")}</span>
                    </div>
                </StyledContainerSignIn>
            ) : (
                <PageUserDetail />
            )}
        </>
    );
};
