import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory, useParams } from "react-router";
import { useTranslation } from "react-i18next";

import { benefitApi, couponApi, merchantApi } from "@api";
import { ComponentBenefitDetail, ComponentCouponDetail } from "@components";
import { PATH_404, PATH_SIGN_IN_EMAIL, enumStatus, enumTypes, PATH_HOME } from "@configs";
import { IBenefit, ICoupon, IMerchant, IMerchantQRcode } from "@interfaces";
import {
    selectAuth,
    setExternalTypes,
    setLoading,
    setMainColor,
    setMerchant,
    setMerchantID,
} from "@redux";
import { useNotify } from "@utils";

export const PageBenefit = () => {
    //page hook
    const { t } = useTranslation();
    const dispatch = useDispatch();
    const { id } = useParams<{ id: string }>();
    //page state
    const [benefit, setBenefit] = useState<IBenefit>();

    useEffect(() => {
        (async () => {
            dispatch(setLoading(true));
            const res = await benefitApi.getDetailBenefitById(id);
            const data = res.data as IBenefit;
            setBenefit(data);

            dispatch(setLoading(false));
        })();
    }, []);

    const handleClick = async () => {
        try {
            dispatch(setLoading(true));
            const res = await benefitApi.redeemed({
                objectId: benefit?._id || "",
                status: enumStatus.ACTIVE,
                type: enumTypes.BENEFIT,
            });
            if (res.data.message) {
                success(res.data.message);
                history.push(PATH_HOME);
            }
        } catch (err: any) {
            const { message } = err.response.data;
            if (message) {
                error(message);
            } else {
                error(t("message.received.fail"));
            }
        } finally {
            dispatch(setLoading(false));
        }
    };

    return <>{benefit && <ComponentBenefitDetail data={benefit} handleClick={handleClick} />}</>;
};
