import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory, useParams } from "react-router";
import { useTranslation } from "react-i18next";

import { freePointApi, merchantApi } from "@api";
import { ComponentFreePointDetail, ComponentPopUpConfirm } from "@components";
import { PATH_404, enumStatus, enumTypes, PATH_HOME, PATH_SIGN_IN } from "@configs";
import { IFreePoint, IMerchant, IMerchantQRcode } from "@interfaces";
import {
    selectAuth,
    setExternalTypes,
    setLoading,
    setMainColor,
    setMerchant,
    setMerchantID,
    setToggleConfirm,
} from "@redux";
import { useNotify } from "@utils";

export const PageFreePoint = () => {
    //page hook
    const { t } = useTranslation();
    const dispatch = useDispatch();
    const { id } = useParams<{ id: string }>();
    const history = useHistory();
    const { error, success } = useNotify();
    //page redux
    const { auth, externalType, showConfirm } = useSelector(selectAuth);
    //page state
    const [freePoint, setFreePoint] = useState<IFreePoint>();
    //page variable
    const token = auth?.accessToken;

    useEffect(() => {
        useFreePoint();
    }, []);

    const useFreePoint = async () => {
        try {
            dispatch(setLoading(true));
            if (id) {
                if (token) {
                    const res = await freePointApi.getPreviewFreePoint(id);
                    const data = res.data as IFreePoint;
                    setFreePoint(data);
                    dispatch(
                        setExternalTypes({
                            type: externalType?.type || enumTypes.FREE_POINT,
                            id: "",
                        })
                    );
                } else {
                    const res = await merchantApi.getFreePointId(id);
                    const data = res.data as IMerchantQRcode;
                    dispatch(setMerchantID(data.merchantId));
                    const merchantConfig = await merchantApi.getMerchantConfig();
                    const merchant = merchantConfig.data.merchant as IMerchant;
                    dispatch(setMerchant(merchant));
                    dispatch(setMainColor(merchant.themeColor));
                    dispatch(setExternalTypes({ type: enumTypes.COUPON, id }));
                    history.push(PATH_SIGN_IN);
                }
            } else {
                history.push(PATH_404);
            }
        } catch {
            history.push(PATH_404);
        } finally {
            dispatch(setLoading(false));
        }
    };

    const handleRedeemedFreePoint = async () => {
        try {
            dispatch(setLoading(true));
            const res = await freePointApi.redeemedFreePoint(
                {
                    objectId: freePoint?._id || "",
                    status: enumStatus.ACTIVE,
                    type: enumTypes.FREE_POINT,
                },
                id
            );
            if (res.data.message) {
                success(res.data.message);
            }
        } catch (err: any) {
            const { message } = err.response.data;
            if (message) {
                error(message);
            } else {
                error(t("message.received.fail"));
            }
        } finally {
            dispatch(setLoading(false));
            history.push(PATH_HOME);
        }
    };

    const handleToggleConfirm = () => {
        dispatch(setToggleConfirm());
    };

    return (
        <>
            {freePoint && (
                <ComponentFreePointDetail
                    freePoint={freePoint}
                    handleToggleConfirm={handleToggleConfirm}
                />
            )}

            {showConfirm && freePoint && (
                <ComponentPopUpConfirm
                    handleCloseDetail={handleToggleConfirm}
                    data={freePoint as IFreePoint}
                    handleConfirm={handleRedeemedFreePoint}
                />
            )}
        </>
    );
};
