import queryString from "querystring";
import { useEffect } from "react";
import { useHistory } from "react-router";
import { useDispatch, useSelector } from "react-redux";

import { enumExternalMethod, PATH_SIGN_UP_SOCIAL_TEL } from "@configs";
import { logout, selectAuth, setExternalLogin } from "@redux";

export const PageLineLogin = () => {
    //page hook
    const dispatch = useDispatch();
    const history = useHistory();
    //redux state
    const { externalLogin } = useSelector(selectAuth);
    //page variable
    const queryParams = queryString.parse(window.location.search.substring(1));

    useEffect(() => {
        if (externalLogin === enumExternalMethod.LINE) {
            // externalLine();
        }
    }, []);

    useEffect(() => {
        if (queryParams.code) {
            if (queryParams.state) {
                dispatch(
                    setExternalLogin({
                        type: enumExternalMethod.LINE,
                        line: {
                            code: queryParams.code.toString(),
                            state: queryParams.state.toString(),
                        },
                    })
                );
            } else {
                dispatch(logout());
            }
        } else {
            dispatch(logout());
        }
    }, [queryParams]);

    return <></>;
};
