import styled from "styled-components";
import { FormikHelpers, useFormik } from "formik";
import { useTranslation } from "react-i18next";
import * as Yup from "yup";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import { StyledSignUp, ComponentFromSocial, ComponentPassword } from "@components";
import { DAYS, enumExternalMethod, MONTHS, PATH_HOME, YEARS } from "@configs";
import { IResetPassword } from "@interfaces";
import { useYup } from "@validations";
import { selectAuth, setExternalLogin, setLoading } from "@redux";
import { authApi } from "@api";
import { useHistory } from "react-router";
import { useNotify } from "@utils";

export const ModuleResetPassword = () => {
    //page hook
    const { t } = useTranslation();
    const { YubResetPassword } = useYup();
    const dispatch = useDispatch();
    const history = useHistory();
    const { error, success } = useNotify();
    //page redux
    const { externalLogin } = useSelector(selectAuth);

    //page variable
    const initialValues: IResetPassword = {
        password: "",
        conformPassword: "",
    };
    const registerSchema = Yup.object().shape(YubResetPassword);

    const handleRegister = async (
        values: IResetPassword,
        { setSubmitting }: FormikHelpers<IResetPassword>
    ) => {
        try {
            setSubmitting(true);
            dispatch(setLoading(true));
            const res = await authApi.createPassword({
                ...values,
            });
            if (res) {
                success(t("message.sign_up.success"));
                history.push(PATH_HOME);
                dispatch(
                    setExternalLogin({
                        type: externalLogin || enumExternalMethod.GOOGLE,
                        isSignUp: false,
                    })
                );
            }
        } catch (errors: any) {
            const errorsResponse = errors.response.data.errors;
            if (Array.isArray(errorsResponse) && errorsResponse.length) {
                error(errorsResponse[0]);
            } else {
                error(t("message.sign_up.fail"));
            }
        } finally {
            setSubmitting(false);
            dispatch(setLoading(false));
        }
        //
    };

    const { values, handleChange, handleBlur, handleSubmit, isSubmitting, errors, touched } =
        useFormik({
            initialValues: initialValues,
            validationSchema: registerSchema,
            onSubmit: handleRegister,
        });

    return (
        <StyledSignUp>
            <StyledContainer>
                <div className="title">
                    {t("page.auth.fill_in_the_information_and_receive_special_benefits")}{" "}
                </div>
            </StyledContainer>
            <ComponentPassword
                handleBlur={handleBlur}
                handleChange={handleChange}
                errors={errors}
                touched={touched}
                values={values}
                handleSubmit={handleSubmit}
                isSubmitting={isSubmitting}
            />
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        max-width: 70%;
        margin: 0 auto 46px auto;
    }
`;
