import { Layout, Spin } from "antd";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import { StyledContainer, StyledPageInner } from "@components";
import { ILayout } from "@interfaces";
import { selectApp, setLoading } from "@redux";

export const LayoutApp = (props: ILayout) => {
    //page hook
    const dispatch = useDispatch();
    //page state
    const loading = useSelector(selectApp).loading;

    useEffect(() => {
        setTimeout(() => {
            dispatch(setLoading(false));
        }, 1000);
    }, []);

    return (
        <Spin spinning={loading} tip="Loading..." size="large">
            <StyledContainer>
                <Layout className="workspace">
                    <Layout.Content>
                        <StyledPageInner>{props.children}</StyledPageInner>
                    </Layout.Content>
                </Layout>
            </StyledContainer>
        </Spin>
    );
};
