import { useSelector } from "react-redux";
import styled, { CSSProperties } from "styled-components";
import { useTranslation } from "react-i18next";
import { Avatar } from "antd";

import { selectAuth } from "@redux";
import { ArrowRight, IConCamera, IConUser } from "@components";

const styleAvatarUser: CSSProperties = {
    transform: "scale(2)",
};

export const ComponentUserSetting = () => {
    //hooks
    const { t } = useTranslation();
    //redux state
    const { userInfo } = useSelector(selectAuth);

    return (
        <StyledUserSetting>
            <div className="image">
                <div className="box_image">
                    {/* {userInfo?.avatar ? (
                        <Avatar alt="Avatar" size={62} src={userInfo?.avatar} />
                    ) : (
                        <IConUser className="ant-avatar" style={styleAvatarUser} />
                    )} */}
                    <IConUser className="ant-avatar" style={styleAvatarUser} />

                    <div className="camera">
                        <IConCamera />
                    </div>
                </div>
            </div>
            <StyledCartUser>
                <div className="user_info">
                    <div className="title">{t("page.user_detail.name")}</div>
                    <div className="desc">{userInfo?.fullName}</div>
                </div>
                <div className="arrowRight">
                    <ArrowRight />
                </div>
            </StyledCartUser>
            <StyledCartUser>
                <div className="user_info">
                    <div className="title">{t("page.user_detail.phone_number")}</div>
                    <div className="desc">{userInfo?.fullName}</div>
                </div>
                <div className="arrowRight">
                    <ArrowRight />
                </div>
            </StyledCartUser>
            <StyledCartUser>
                <div className="user_info">
                    <div className="title">{t("page.user_detail.email")}</div>
                    <div className="desc">{userInfo?.fullName}</div>
                </div>
                <div className="arrowRight">
                    <ArrowRight />
                </div>
            </StyledCartUser>
            <StyledCartUser>
                <div className="user_info">
                    <div className="title">{t("page.user_detail.date")}</div>
                    <div className="desc">{userInfo?.fullName}</div>
                </div>
                <div className="arrowRight">
                    <ArrowRight />
                </div>
            </StyledCartUser>
            <div className="line">
                <StyledCartUser>
                    <div className="user_info">
                        <div className="desc">{t("page.user_detail.link_different")}</div>
                    </div>
                    <div className="arrowRight">
                        <ArrowRight />
                    </div>
                </StyledCartUser>
            </div>
            <div className="line_desc">{t("page.user_detail.request_link")}</div>
        </StyledUserSetting>
    );
};

const StyledUserSetting = styled.div`
    .image {
        width: 100%;
        padding: 46px 0;
        background: #f7f7f8;
        /* background: #000; */
        text-align: center;
        img {
            border-radius: 50%;
        }
    }
    .box_image {
        position: relative;
        width: fit-content;
        margin: 0 auto;
    }
    .camera {
        position: absolute;
        bottom: 0;
        right: 0;
    }
    .line {
        margin-top: 42px;
    }
    .line_desc {
        margin: 16px 12px;
        font-family: Segoe UI;
        font-style: normal;
        font-weight: normal;
        font-size: 14px;
        color: #707070;
    }
`;

const StyledCartUser = styled.div`
    padding: 9px 16px;
    background-color: #fff;
    border-bottom: 1px solid #eeeeee;
    display: flex;
    justify-content: space-between;
    align-items: center;
    .arrowRight {
        cursor: pointer;
    }
    &:last-child {
        border-bottom: initial;
    }
    .user_info {
        .title {
            font-family: Segoe UI;
            font-style: normal;
            font-weight: 600;
            font-size: 14px;
            color: #707070;
            margin-bottom: 4px;
        }
        .desc {
            font-family: Leelawadee UI;
            font-style: normal;
            font-weight: bold;
            font-size: 18px;
            color: #000000;
        }
    }
`;
