import { useSelector } from "react-redux";
import styled from "styled-components";
import { useTranslation } from "react-i18next";
import { useHistory } from "react-router";

import { IExternalAuthState } from "@interfaces";
import { StyledContainerSignIn, StyledSignUp } from "..";
import { ModuleSocialMedia } from "@modules";
import { enumExternalAuthType, PATH_SIGN_IN_EMAIL } from "@configs";
import { selectMerchant } from "@redux";

export const ComponentSocial = () => {
    //page hook
    const { t } = useTranslation();
    const history = useHistory();
    // redux state
    const merchant = useSelector(selectMerchant);

    const state: IExternalAuthState = {
        type: enumExternalAuthType.SIGNUP,
    };

    const handleSignIn = () => {
        history.push(PATH_SIGN_IN_EMAIL);
    };

    return (
        <StyledContainerSignIn color={merchant.themeColor}>
            <StyledSignUp>
                <StyledContainer>
                    <div className="title">
                        {t("page.auth.sign_up_to")} {merchant.name}
                    </div>
                    <div className="choose">{t("page.auth.choose_sign_up_method")}</div>
                </StyledContainer>
                <ModuleSocialMedia state={state} />
            </StyledSignUp>
            <div className="sign_in">
                {t("page.auth.already_have_an_account")}{" "}
                <span onClick={handleSignIn}>{t("page.auth.sign_in")}</span>
            </div>
        </StyledContainerSignIn>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
`;
