import { useSelector } from "react-redux";
import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { useHistory } from "react-router";

import { selectMerchant } from "@redux";
import { SharedButton, StyledWelcome } from "@components";
import { PATH_SIGN_IN_EMAIL } from "@configs";

export const PageWelcome = () => {
    //page hook
    const { t } = useTranslation();
    const history = useHistory();
    //redux state
    const { themeColor, welcomeImageUrls, name } = useSelector(selectMerchant);

    const handleClick = () => {
        history.push(PATH_SIGN_IN_EMAIL);
    };
    console.log(name);
    return (
        <StyledWelcome themeColor={themeColor}>
            <div className="box_top">
                <div className="welcome">{t("page.welcome.welcome")} !</div>
                <div className="desc">
                    name
                    {t("page.welcome.desc_welcome", {
                        returnObjects: true,
                        name,
                    })}{" "}
                    !
                </div>
                <StyledButton size="large" bg_color={themeColor} onClick={handleClick}>
                    {t("page.welcome.become")}
                </StyledButton>
            </div>
            <div className="group_images">
                {welcomeImageUrls?.map((item) => {
                    return <img key={item} src={item} />;
                })}
            </div>
        </StyledWelcome>
    );
};

const StyledButton = styled((props) => <SharedButton {...props} />)`
    width: 90% !important;
`;
