import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";

import { ComponentHeaderAuth, ComponentSignUpEmail } from "@components";
import { ComponentFaceBookButton, ComponentGoogleButton, ComponentLineButton } from "@components";
import { logout, selectMerchant } from "@redux";
import { enumExternalAuthType } from "@configs";
import { IExternalAuthState } from "@interfaces";
import { ModuleGoogle } from "@modules";

export const PageSignUpEmail = () => {
    //page hook
    const { t } = useTranslation();
    const dispatch = useDispatch();
    //page variable
    const merchant = useSelector(selectMerchant);
    const state: IExternalAuthState = {
        type: enumExternalAuthType.LOGIN,
    };

    const errorHandler = (error: any) => {
        //handle Error
        if (error.response) {
            const { status, data } = error.response;
            if (status === 401) {
                // dispatch(
                //     openToast({
                //         message: data?.message,
                //         type: "error",
                //         autoHideDuration: 2000,
                //     })
                // );
            } else if (status === 500) {
                // dispatch(
                //     openToast({
                //         message: data?.message,
                //         type: "error",
                //         autoHideDuration: 2000,
                //     })
                // );
            } else if (data?.message) {
                // dispatch(
                //     openToast({
                //         message: data?.message,
                //         type: "error",
                //         autoHideDuration: 2000,
                //     })
                // );
            } else {
                //     dispatch(
                //         openToast({
                //             message: t("page.auth.login_failed"),
                //             type: "error",
                //             autoHideDuration: 2000,
                //         })
                //     );
                // }
            }
            // else {
            //     dispatch(
            //         openToast({
            //             message: t("page.auth.login_failed"),
            //             type: "error",
            //             autoHideDuration: 2000,
            //         })
            //     );
            // };
            dispatch(logout());
        }
    };

    const socials: JSX.Element[] = [
        <ComponentFaceBookButton />,
        <ModuleGoogle errorHandler={errorHandler} state={state} />,
        <ComponentLineButton />,
    ];

    return (
        <StyledContainer color={merchant.themeColor}>
            <ComponentHeaderAuth />
            <ComponentSignUpEmail socials={socials} />
            <div className="sign_in">
                {t("page.sign_up.already_have_an_account")} <span>{t("page.sign_up.sign_in")}</span>
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div<{ color: string }>`
    .sign_in {
        position: relative;
        top: -85px;
        text-align: end;
        margin: 36px 36px 0 0;
        font-style: normal;
        font-weight: bold;
        font-size: 16px;
        line-height: 21px;
        span {
            color: ${(p) => p.color};
            cursor: pointer;
        }
    }
`;
