import styled from "styled-components";
import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";

import { IMembershipTier } from "@interfaces";
import { IConBecomeStar, IConCircle, IConClose } from "@components";
import { selectMerchant } from "@redux";

interface IProps {
    membershipTier: IMembershipTier;
    handleClose: () => void;
}

export const ComponentCurrentPoint = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const { membershipTier, handleClose } = props;
    const { themeColor } = useSelector(selectMerchant);

    return (
        <StyledCurrentPoint borderBottom={!!membershipTier?.benefits?.length}>
            <div className="content_currentPoint">
                <div className="close" onClick={handleClose}>
                    <IConClose />
                </div>
                <div className="title">
                    <div className="image">
                        <IConBecomeStar color={themeColor} />
                    </div>
                    <div className="member">
                        {membershipTier.membershipName} {t("page.member_shipTier.member")}
                    </div>
                </div>
                <div className="baht_spent">
                    {membershipTier.bahtSpent}à¸¿ {t("page.member_shipTier.spent")} ={" "}
                    {membershipTier.points} {t("page.member_shipTier.point")}
                </div>
                {membershipTier?.benefits?.length !== 0 && (
                    <div className="benefits">
                        {membershipTier?.benefits?.map((benefit) => {
                            return (
                                <>
                                    <div className="circle">
                                        <IConCircle color={themeColor} />
                                        <span>{benefit}</span>
                                    </div>
                                    <div className="circle">
                                        <IConCircle color={themeColor} />
                                        <span>{benefit}</span>
                                    </div>
                                </>
                            );
                        })}
                    </div>
                )}
            </div>
        </StyledCurrentPoint>
    );
};

const StyledCurrentPoint = styled.div<{ borderBottom: boolean }>`
    height: 100vh;
    width: 100%;
    position: fixed;
    top: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: 480px;
    background: rgba(0, 0, 0, 0.5);
    font-family: "Segoe UI", "Roboto", sans-serif;
    .content_currentPoint {
        width: 90%;
        padding: 28px;
        background-color: #fff;
        position: relative;
        border-radius: 8px;
        max-height: 90%;
        overflow: scroll;
    }
    .close {
        position: absolute;
        top: 20px;
        right: 20px;
        cursor: pointer;
    }
    .title {
        display: flex;
        align-items: center;
        .image {
            width: 28px;
            margin-right: 8px;
        }
        .member {
            font-family: "Segoe UI", "Roboto", sans-serif;
            font-style: normal;
            font-weight: 600;
            font-size: 20px;
            color: #000000;
        }
    }
    .baht_spent {
        font-family: "Segoe UI", "Roboto", sans-serif;

        font-style: normal;
        font-weight: normal;
        font-size: 18px;
        color: #7e7e7e;
        padding: 8px 0 16px 0;
        ${(p) => {
            if (p.borderBottom) return "border-bottom: 1px solid rgba(112, 112, 112, 0.31);";
        }}
    }
    .benefits {
        display: flex;
        align-items: center;
        margin-top: 8px;
        .circle {
            font-family: "Segoe UI", "Roboto", sans-serif;

            font-style: normal;
            font-weight: normal;
            font-size: 16px;
            line-height: 1.5rem;
            color: #000000;
            span {
                display: inline-block;
                margin-left: 12px;
            }
        }
    }
`;
