import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { useSelector } from "react-redux";

import { ComponentHeaderAuth, ComponentSignInEmail } from "@components";
import { ComponentFaceBookButton, ComponentGoogleButton, ComponentLineButton } from "@components";
import { selectMerchant } from "@redux";

export const PageResetPasswordEmail = () => {
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);
    const socials: JSX.Element[] = [
        <ComponentFaceBookButton />,
        <ComponentGoogleButton />,
        <ComponentLineButton />,
    ];

    return (
        <StyledContainer color={merchant.themeColor}>
            <ComponentHeaderAuth />
            <div className="sign_in">
                {t("page.sign_up.already_have_an_account")} <span>{t("page.sign_up.sign_in")}</span>
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div<{ color: string }>`
    .sign_in {
        position: relative;
        top: -85px;
        text-align: end;
        margin: 36px 36px 0 0;
        font-style: normal;
        font-weight: bold;
        font-size: 16px;
        line-height: 21px;
        span {
            color: ${(p) => p.color};
            cursor: pointer;
        }
    }
`;
