import styled from "styled-components";
import moment from "moment";
import { useTranslation } from "react-i18next";

import { ICoupon } from "@interfaces";
import { exchangedType } from "@configs";

interface IProps {
    data: ICoupon;
    handleClick: (id: string) => void;
}

export const ComponentHistoryCoupon = (props: IProps) => {
    //page hooks
    const { t } = useTranslation();
    //page props
    const { data, handleClick } = props;
    const date = moment(data.updatedAtHistory).format("DD MMM YYYY");

    return (
        <StyledContainer
            onClick={() => handleClick(data.idCart || "")}
            isRECEIVED={data.exchangedType === exchangedType.RECEIVED}
        >
            <div className="coupon">
                <div className="title">
                    {t("page.history.coupon_title", {
                        name: data.benefit,
                    })}
                </div>
                <div className="date">
                    <span className="status">
                        {data.exchangedType === exchangedType.RECEIVED
                            ? t("page.history.collected")
                            : t("page.history.used")}
                    </span>
                    <span className="time">{date}</span>
                </div>
            </div>
            <div className="quality">
                {" "}
                {data.exchangedType === exchangedType.RECEIVED ? "+" : "-"} 1
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div<{ isRECEIVED: boolean }>`
    padding: 18px 14px;
    display: flex;
    justify-content: space-between;
    align-items: end;
    background-color: #fff;
    margin-bottom: 16px;
    cursor: pointer;
    .coupon {
        .title {
            font-weight: 600;
            font-size: 16px;
            line-height: 21px;
            color: #000000;
            margin-bottom: 4px;
        }
    }
    .date {
        font-weight: normal;
        font-size: 8px;
        line-height: 11px;
        color: #707070;
        .status {
            display: inline-block;
            padding-right: 8px;
            margin-right: 8px;
            border-right: 0.5px solid #707070;
        }
    }
    .quality {
        color: ${(p) => (p.isRECEIVED ? "#378A00" : "#970000")};
        font-weight: 600;
        font-size: 20px;
        line-height: 27px;
    }
`;
