import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";

import { selectAuth, selectMerchant } from "@redux";
import { IConBecomeBenefit, IConBecomeCoupon, IConBecomeStar } from "@components";
import { ModuleMemberShipTier } from "@modules";

export const ComponentUserDetail = () => {
    //hooks
    const { t } = useTranslation();
    //redux page
    const { name, themeColor, welcomeImageUrls } = useSelector(selectMerchant);
    const { userInfo } = useSelector(selectAuth);
    //page variable
    const memberTier = userInfo?.memberTier;

    return (
        <StyledUserDetail>
            <StyledCart>
                <div className="become">{t("page.user_detail.become_member")}</div>
                <div className="desc">
                    {t("page.user_detail.fill_in_your_information")}
                    {name} {t("page.user_detail.and_receive")}
                </div>
                <div className="group">
                    <div className="item">
                        <IConBecomeStar color={themeColor} />
                        <span>{t("page.user_detail.star_desc")}</span>
                    </div>
                    <div className="item">
                        <IConBecomeCoupon color={themeColor} />
                        <span>{t("page.user_detail.coupon_desc")}</span>
                    </div>
                    <div className="item">
                        <IConBecomeBenefit color={themeColor} />
                        <span>{t("page.user_detail.benefit_desc")}</span>
                    </div>
                    <div className="item border-none">
                        <IConBecomeStar color={themeColor} />
                        <span>{t("page.user_detail.draws_desc")}</span>
                    </div>
                </div>
            </StyledCart>
            <StyledCart>
                <div className="content">
                    <div className="title_rule">{t("page.user_detail.rules_of_points")}</div>
                    <div className="points">
                        <div className="left">
                            <StyledButtonPoint themeColor={themeColor}>
                                {memberTier?.bahtSpent || 0}
                            </StyledButtonPoint>
                            <span>{t("page.user_detail.baht_spent")}</span>
                        </div>
                        <div className="equal">=</div>
                        <div className="right">
                            <StyledButtonPoint themeColor={themeColor}>
                                {memberTier?.initPoint || 0}
                            </StyledButtonPoint>
                            <span>{t("page.user_detail.points")}!</span>
                        </div>
                    </div>
                </div>
            </StyledCart>
            <ModuleMemberShipTier />
            <div className="group_image">
                {welcomeImageUrls?.map((welcomeImageUrl) => {
                    return <img className="welcomeImageUrl_image" src={welcomeImageUrl}></img>;
                })}
            </div>
        </StyledUserDetail>
    );
};

const StyledCart = styled.div`
    width: 88%;
    margin: 0 auto;
    padding: 0 24px 32px 24px;
    background-color: #fff;
    box-shadow: rgb(215 215 215) 0px 0px 4px 0px;
    border-radius: 12px;
    margin-bottom: 20px;
    .become {
        text-align: center;
        padding-top: 36px;
        font-family: "Segoe UI", "Roboto", sans-serif;
        font-style: normal;
        font-weight: 600;
        font-size: 20px;
        color: #000000;
        margin-bottom: 24px;
    }
    .desc {
        text-align: center;
        font-size: 18px;
        color: #7e7e7e;
        line-height: 1.5rem;
        font-family: "Leelawadee UI", "Roboto", sans-serif;
        font-style: normal;
        font-weight: normal;
        margin-bottom: 6px;
    }
    .group {
        .item {
            padding: 24px 0;
            border-bottom: 1px solid rgba(115, 115, 115, 0.31);
            display: flex;
            align-items: center;
            span {
                display: inline-block;
                margin-left: 20px;
                font-family: "Segoe UI", "Roboto", sans-serif;
                font-style: normal;
                font-weight: normal;
                font-size: 16px;
                color: #000000;
                font-weight: 500;
            }
        }
    }
    .border-none {
        border-bottom: initial !important;
    }
    .title_rule {
        font-family: "Segoe UI", "Roboto", sans-serif;
        font-style: normal;
        font-weight: 600;
        font-size: 20px;
        color: #000000;
        margin-bottom: 32px;
    }
    .content {
        text-align: center;
        padding: 44px 0;
        .points {
            display: flex;
            justify-content: center;
            align-items: center;
            span {
                font-family: "Leelawadee UI", "Roboto", sans-serif;
                font-style: normal;
                font-weight: normal;
                font-size: 16px;
                color: #000000;
            }
        }
        .equal {
            margin: 0 36px;
            position: relative;
            top: -11px;
        }
    }
`;

const StyledButtonPoint = styled.div<{ themeColor: string }>`
    width: fit-content;
    padding: 6px 24px;
    background: #f5f5f5;
    color: ${(p) => p.themeColor};
    border-radius: 4px;
    margin-bottom: 6px;
    font-weight: 600;
`;

const StyledUserDetail = styled.div`
    background: linear-gradient(180deg, transparent 0%, #f8f8f8);
    padding-bottom: 50px;
    .group_image {
        text-align: center;
    }
    .welcomeImageUrl_image {
        width: 88%;
        margin: 12px auto;
        height: 380px;
        object-fit: cover;
    }
`;
