import { Layout, Spin } from "antd";
import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";

import { StyledContainer, ComponentHeader, ComponentSidebar, StyledPageInner } from "@components";
import { setMobile } from "@redux";
import { ILayout } from "@interfaces";


export const LayoutApp = (props: ILayout) => {
    //page state
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        setTimeout(() => {
            setLoading(false);
        }, 1000);
    }, []);

    return (
        <Spin spinning={loading} tip="Loading..." size="large">
            <StyledContainer>
                {/* {!isNotDashboard && <ComponentHeader />} */}
                <ComponentHeader />
                <Layout className="workspace">
                    {/* {!isNotDashboard && (
                        <ComponentSidebar
                            sidebarTheme={state.darkSidebar ? "dark" : "light"}
                            sidebarMode={state.sidebarPopup ? "vertical" : "inline"}
                            sidebarIcons={state.sidebarIcons}
                            collapsed={state.collapsed}
                        />
                    )} */}
                    <ComponentSidebar
                        sidebarTheme={"light"}
                        sidebarMode={"inline"}
                        sidebarIcons={true}
                        collapsed={false}
                    />

                    <Layout>
                        <Layout.Content>
                            {/* {!isNotDashboard ? <Inner>{props.children}</Inner> : props.children} */}
                            <StyledPageInner>{props.children}</StyledPageInner>
                        </Layout.Content>
                    </Layout>
                </Layout>
            </StyledContainer>
        </Spin>
    );
};
