import { useTranslation } from "react-i18next";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { Navigation, Pagination } from "swiper";
import "swiper/swiper-bundle.min.css";
import "swiper/swiper.min.css";
import "swiper/components/navigation/navigation.min.css";
import Parser from "html-react-parser";
import { useSelector } from "react-redux";
import styled from "styled-components";

import { BLANK_IMAGE_URL } from "@configs";
import { ArrowRight, IconStarSub, SharedButton, StyledPreview } from "@components";
import { IReward, IVariant } from "@interfaces";
import { selectAuth, selectMerchant } from "@redux";

interface IProps {
    data: IReward;
    count: number;
    handleToggleConfirm: () => void;
    handleAddCount: () => void;
    handleMinusCount: () => void;
    handleChangeImage: (swiper: SwiperCore) => void;
    currentImage: number;
    handleToggleShowSelectType: () => void;
    variant: string;
    variants: IVariant[];
}

SwiperCore.use([Navigation, Pagination]);

export const ComponentRewardDetail = (props: IProps) => {
    //hook
    const { t } = useTranslation();
    // props
    const {
        data,
        handleToggleConfirm,
        count,
        handleAddCount,
        handleMinusCount,
        currentImage,
        handleChangeImage,
        handleToggleShowSelectType,
        variant,
        variants,
    } = props;
    //redux states
    const { themeColor } = useSelector(selectMerchant);
    const { showConfirm, userInfo } = useSelector(selectAuth);

    return (
        <StyledPreview showConfirm={showConfirm}>
            <div className="page_body">
                {data.imageUrl.length ? (
                    <div className="pagination">
                        {currentImage}/{data.imageUrl.length}
                    </div>
                ) : (
                    <></>
                )}
                <div className="img-field">
                    <Swiper slidesPerView={1} onActiveIndexChange={handleChangeImage}>
                        {data.imageUrl.length ? (
                            data.imageUrl.map((item, index) => (
                                <SwiperSlide key={index}>
                                    <div className="img-wrap">
                                        <img src={item} />
                                    </div>
                                </SwiperSlide>
                            ))
                        ) : (
                            <SwiperSlide>
                                <div className="img-wrap">
                                    <img className="empty-img" src={BLANK_IMAGE_URL} />
                                </div>
                            </SwiperSlide>
                        )}
                    </Swiper>
                </div>
                <div className="name-field">
                    <p>
                        {t("page.reward.quantity_available", {
                            returnObjects: true,
                            number: data.quantity,
                        })}
                    </p>
                    <h5>{data.name}</h5>
                </div>
                <div className="quantity-field">
                    <div className="count-item">
                        <div className="item-left">
                            <div className="item-left-top">
                                <IconStarSub color={themeColor} />
                                <p>
                                    {data.point} x {count}
                                </p>
                            </div>

                            {userInfo?.totalPoints && (
                                <div className="text-small">
                                    {t("page.reward.have_points_left", {
                                        returnObjects: true,
                                        number: userInfo?.totalPoints,
                                    })}
                                </div>
                            )}
                        </div>
                        <div className="item-right">
                            <button onClick={handleMinusCount} className=" btn-minus">
                                -
                            </button>
                            <p>{count}</p>
                            <button onClick={handleAddCount} className=" btn-add">
                                +
                            </button>
                        </div>
                    </div>
                </div>
                {variants.length !== 0 && (
                    <div className="select-field">
                        <div className="text-small">
                            <div className="select_type">{t("page.reward.select_type")}</div>
                            {variant && <div className="variant">{variant}</div>}
                        </div>
                        <div className="arrowRight" onClick={handleToggleShowSelectType}>
                            <ArrowRight />
                        </div>
                    </div>
                )}
                <div className="description-field">
                    {data.description && (
                        <>
                            <h5>{t("page.reward.descriptions")}</h5>
                            <div>{Parser(data.description)}</div>
                        </>
                    )}
                    <div className="use_point">
                        <StyledButton
                            size="large"
                            bg_color={themeColor}
                            type="submit"
                            onClick={handleToggleConfirm}
                        >
                            {t("page.reward.use_points")}
                        </StyledButton>
                    </div>
                </div>
            </div>
        </StyledPreview>
    );
};

const StyledButton = styled(SharedButton)`
    width: 95%;
    margin: 24px auto;
`;
