import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";

import { selectMerchant } from "@redux";
import { ArrowRight, IConBecomeStar } from "@components";

interface IProps {
    handleClickNext: (id: string) => void;
}

export const ComponentMembershipTier = (props: IProps) => {
    //hooks
    const { t } = useTranslation();
    //redux page
    const { membershipTiers, themeColor } = useSelector(selectMerchant);
    //page props
    const { handleClickNext } = props;

    return (
        <StyledMemberShipTier>
            <div className="title">{t("page.member_shipTier.membership_tier")}</div>
            <div className="desc">{t("page.member_shipTier.desc")}</div>
            {membershipTiers?.map((membershipTier) => {
                return (
                    <>
                        {!membershipTier.isDefault && (
                            <div className="membershipTiers" key={membershipTier._id}>
                                <div className="membershipTiers__info">
                                    <div className="image">
                                        <IConBecomeStar color={themeColor} />
                                    </div>
                                    <div className="rank">
                                        <div className="name">{membershipTier.membershipName}</div>
                                        <div className="membershipTiers_desc">
                                            {t("page.member_shipTier.earn_points_in_a_year", {
                                                point: membershipTier.points,
                                            })}
                                        </div>
                                    </div>
                                </div>
                                <div
                                    className="arrow_right"
                                    onClick={() => handleClickNext(membershipTier._id)}
                                >
                                    <ArrowRight />
                                </div>
                            </div>
                        )}
                    </>
                );
            })}
        </StyledMemberShipTier>
    );
};

const StyledMemberShipTier = styled.div`
    width: 88%;
    margin: 0 auto;
    padding: 0 24px 32px 24px;
    background-color: #fff;
    box-shadow: rgb(231 230 230) 0px 5px 14px 0px;
    border-radius: 12px;
    .title {
        font-family: "Segoe UI", "Roboto", sans-serif;
        font-style: normal;
        font-weight: 600;
        font-size: 20px;
        color: #000000;
        padding: 28px 0 18px 0;
    }
    .desc {
        font-family: "Segoe UI", "Roboto", sans-serif;
        font-style: normal;
        font-weight: normal;
        line-height: 1.5rem;
        font-size: 16px;
        color: #000000;
        margin-bottom: 30px;
    }
    .membershipTiers {
        display: flex;
        padding: 12px 12px 12px 0;
        align-items: center;
        justify-content: space-between;
        border-bottom: 1px solid rgba(115, 115, 115, 0.31);
        .membershipTiers__info {
            display: flex;
            align-items: center;
        }
        .rank {
            margin-left: 16px;
            .name {
                font-family: "Segoe UI", "Roboto", sans-serif;
                font-style: normal;
                font-weight: 600;
                font-size: 18px;
                line-height: 1.5rem;
                color: #000000;
                margin-bottom: 4px;
            }
            .membershipTiers_desc {
                font-family: "Segoe UI", "Roboto", sans-serif;
                font-style: normal;
                font-weight: normal;
                font-size: 18px;
                line-height: 24px;
                color: #7b7b7b;
            }
        }
    }
    .membershipTiers:last-child {
        border-bottom: initial;
    }
    .arrow_right {
        cursor: pointer;
    }
    .image {
        width: 28px;
    }
`;
