import { IConClose } from "@components";
import { useTranslation } from "react-i18next";
import styled from "styled-components";

export const ComponentHistoryDetail = () => {
    //page hooks
    const { t } = useTranslation();

    return (
        <StyledContainer>
            <div className="content">
                <div className="close">
                    <IConClose />
                </div>
                <div className="confirmation">{t("page.history.confirmation")}</div>
                <div className="title">{t("page.history.name")}</div>
                <div className="desc">David</div>
                <div className="title">{t("page.history.item")}</div>
                <div className="desc">
                    Redemption (กล้วยทอดเกาหลี ซุปเปอร์ พรีเมี่ยม ห่อเล็ก 200 points)
                </div>
                <div className="title">{t("page.history.ticket")}</div>
                <div className="desc">1029383</div>
                <div className="title">{t("page.history.date_time")}:</div>
                <div className="desc">10/01/2021 19:38:10</div>
                <div className="title">10/01/2021 19:38:10</div>
                <StyledButton
                    size="large"
                    bg_color={themeColor}
                    type="submit"
                    onClick={handleUseCoupon}
                >
                    {t("page.reward.use_points")}
                </StyledButton>
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    height: 100vh;
    width: 100%;
    position: fixed;
    top: 0;
    display: flex;
    /* text-align: center; */
    align-items: center;
    justify-content: center;
    max-width: 480px;
    background: rgba(0, 0, 0, 0.5);
    .content {
        width: 90%;
        padding: 28px;
        background-color: #fff;
        position: relative;
        border-radius: 8px;
        .close {
            position: absolute;
            top: 20px;
            right: 20px;
            cursor: pointer;
        }
    }
    .confirmation {
        font-weight: 600;
        font-size: 18px;
        line-height: 24px;
        color: #000000;
        padding-bottom: 14px;
        border-bottom: 1px solid #707070;
    }
    .title {
        margin: 24px 0 4px 0;
        font-weight: bold;
        font-size: 14px;
        line-height: 19px;
        color: #989898;
    }
    .desc {
        font-weight: 600;
        font-size: 16px;
        color: #000000;
    }
`;

const StyledButton = styled(SharedButton)`
    width: 90%;
    margin: 24px auto;
`;
