import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";

import { rewardApi } from "@api";
import { ComponentRewardList } from "@components";
import { IReward, IDataReward } from "@interfaces";
import { setLoading } from "@redux";
import { REWARD } from "@configs";

export const ModulesReward = () => {
    const dispatch = useDispatch();
    //page state
    const [reward, setReward] = useState<IDataReward[]>([]);

    useEffect(() => {
        (async () => {
            dispatch(setLoading(true));
            const res = await rewardApi.getListReward();
            const data = res.data.docs as IReward[];
            //WHAT: xu ly data thang IData
            const quantities = data.map((item) => item.quantity) as number[];
            const quantitiesSet = Array.from(new Set(quantities));
            const result = quantitiesSet.map((item) => {
                const res = data.filter((values) => values.quantity === item);
                return {
                    quantity: item,
                    reward: res,
                };
            });
            setReward(result);
            // setReward(data);
            dispatch(setLoading(false));
        })();
    }, []);

    const handleClick = (value: IReward) => {
        if (value.levelIds?.length) return;
        history.push(REWARD + "/" + value._id);
        //
    };

    return <>{reward && <ComponentRewardList data={reward} handleClick={handleClick} />}</>;
};
