import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { useHistory, useParams } from "react-router";

import { setMainColor, setMerchant, setMerchantID } from "@redux";
import { merchantApi } from "@api";
import { IMerchant, IMerchantQRcode } from "@interfaces";
import { enumStatus, PATH_404, PATH_WELCOME } from "@configs";

export const PageClientLink = () => {
    //page hook
    const dispatch = useDispatch();
    const { id } = useParams<{ id: string }>();
    const history = useHistory();

    useEffect(() => {
        merchantConfig();
    }, []);

    const merchantConfig = async () => {
        if (id) {
            dispatch(setMerchantID(id));
            const merchantConfig = await merchantApi.getMerchantConfig();
            const merchant = merchantConfig.data.merchant as IMerchant;
            dispatch(setMerchant(merchant));
            dispatch(setMainColor(merchant.themeColor));
            history.push(PATH_WELCOME);
        } else {
            history.push(PATH_404);
        }
    };
    return <></>;
};
