import styled, { CSSProperties } from "styled-components";
import { useSelector } from "react-redux";
import { useTranslation } from "react-i18next";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

import { IMemberTier } from "@interfaces";
import { selectAuth, selectMerchant } from "@redux";
import { ModuleMemberShipTier } from "@modules";
import { IConUser } from "@components";

interface IProps {
    avatar: string;
    fullName: string;
    memberTier: IMemberTier | undefined;
}

const styleAvatarUser: CSSProperties = {
    transform: "scale(1.5)",
    margin: "0 12px",
};

export const ComponentTIerDetail = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const { avatar, fullName, memberTier } = props;
    //redux state
    const { membershipTiers, themeColor } = useSelector(selectMerchant);
    const { userInfo } = useSelector(selectAuth);
    //page variable
    const totalPoints = userInfo?.totalPoints || 0;
    const membershipTier = membershipTiers?.find(
        (membershipTier) => membershipTier.points > totalPoints
    );
    const present = Math.round((+totalPoints / +(membershipTier?.points || 1)) * 100);

    return (
        <StyledTierDetail color={memberTier?.color || "#000"}>
            <div className="avatar">
                <div className="image">
                    {avatar ? (
                        <img src={avatar} />
                    ) : (
                        <IConUser className="ant-avatar" style={styleAvatarUser} />
                    )}
                </div>
                <div className="info">
                    <div className="name">{fullName}</div>
                    <div className="rank">
                        <img src={memberTier?.iconUrl} />
                        <span> {memberTier?.tierName}</span>
                    </div>
                </div>
            </div>
            <div className="content">
                <div className="square">
                    <CircularProgressbar
                        value={present}
                        strokeWidth={8}
                        styles={buildStyles({
                            pathColor: themeColor,
                        })}
                    />
                    <div className="text">
                        {totalPoints !== 0 ? (
                            membershipTier ? (
                                <>
                                    <div className="point">
                                        {totalPoints} {t("page.user_detail.of")}{" "}
                                        {membershipTier?.points}
                                    </div>
                                    <div className="points">{t("page.user_detail.points")}</div>
                                    <div className="to_level">
                                        {t("page.user_detail.to_level", {
                                            rank: membershipTier?.membershipName,
                                        })}
                                    </div>
                                </>
                            ) : (
                                <div className="point">
                                    {totalPoints} {t("page.user_detail.points")}
                                </div>
                            )
                        ) : (
                            <div className="point">
                                {totalPoints}
                                {t("page.user_detail.point")}
                            </div>
                        )}
                    </div>
                </div>
                <ModuleMemberShipTier />
            </div>
        </StyledTierDetail>
    );
};

const StyledTierDetail = styled.div<{ color: string }>`
    display: flex;
    flex-direction: column;
    min-height: 100vh;
    .avatar {
        padding: 32px 0;
        background-color: #fff;
        display: flex;
        align-items: center;
        @media (max-width: ${(p) => p.theme.breakPoints.breakMobile}) {
            padding: 20px 0;
        }
        .image {
            margin: 0 16px;
            img {
                border-radius: 50%;
                width: 94px;
                height: 94px;
                object-fit: cover;
                @media (max-width: ${(p) => p.theme.breakPoints.breakMobile}) {
                    width: 76px;
                    height: 76px;
                }
            }
        }
        .info {
            .name {
                font-family: "Leelawadee UI", "Roboto", sans-serif;
                font-style: normal;
                font-weight: bold;
                font-size: 20px;
                color: #000000;
            }
            .rank {
                border-radius: 16px;
                padding: 4px 6px;
                font-style: normal;
                font-weight: bold;
                font-size: 8px;
                background-color: ${(p) => p.color};
                width: fit-content;
                color: #fff;
                span {
                    display: inline-block;
                    margin-right: 4px;
                    font-family: "Segoe UI", "Roboto", sans-serif;
                    font-weight: bold;
                    font-size: 8px;
                }
            }
        }
    }
    .content {
        flex: 1;
        background: #f7f7f8;
        padding-bottom: 46px;
        .square {
            position: relative;
            height: fit-content;
            text-align: center;
            padding: 50px 0;
            @media (max-width: ${(p) => p.theme.breakPoints.breakMobile}) {
                padding: 42px 0;
            }
            img {
                width: 100%;
            }
            .text {
                position: absolute;
                top: 0;
                left: 0;
                display: flex;
                justify-content: center;
                align-items: center;
                flex-direction: column;
                width: 100%;
                height: 100%;
                .point {
                    font-family: "Roboto", sans-serif;
                    font-style: normal;
                    font-weight: bold;
                    font-size: 25px;
                    color: #000000;
                }
                .points {
                    margin-top: -4px;
                }
                .to_level {
                    font-family: "Roboto", sans-serif;
                    font-style: normal;
                    font-weight: normal;
                    font-size: 12px;
                    color: #9a9a9a;
                }
            }
        }
    }
    .CircularProgressbar {
        width: 60%;
    }
`;
