import { useTranslation } from "react-i18next";
import { useDispatch, useSelector } from "react-redux";
import Parser from "html-react-parser";
import moment from "moment";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { Navigation } from "swiper";
import styled, { CSSProperties } from "styled-components";
import "swiper/swiper-bundle.min.css";
import "swiper/swiper.min.css";
import "swiper/components/navigation/navigation.min.css";
import { useEffect } from "react";

import { SharedButton, StyledPreview } from "@components";
import { resetConfirm, selectAuth, selectMerchant } from "@redux";
import { ICoupon } from "@interfaces";
import { BLANK_IMAGE_URL } from "@configs";

interface IProps {
    coupon: ICoupon;
    handleReceiveCoupon: () => void;
    style?: CSSProperties;
    handleToggleConfirm: () => void;
}

SwiperCore.use([Navigation]);

export const ComponentCouponDetail = (props: IProps) => {
    //page props
    const { coupon, handleReceiveCoupon, style, handleToggleConfirm } = props;
    // const { under, onToggleUnder } = props;
    //page Hooks
    const { t } = useTranslation();
    const { themeColor, subColor } = useSelector(selectMerchant);
    const { showConfirm } = useSelector(selectAuth);
    const dispatch = useDispatch();

    //page variable
    const dateFormFormat = moment(coupon?.startDate).format("yyyy/MM/DD HH:mm");
    const dateToFormat = moment(coupon?.endDate).format("yyyy/MM/DD HH:mm");

    useEffect(() => {
        dispatch(resetConfirm());
    }, []);

    return (
        <StyledPreview
            mainColor={themeColor}
            subColor={subColor}
            style={style}
            showConfirm={showConfirm}
        >
            <div className="page_body">
                <div className="body">
                    <div className="text_name">
                        <div className="name">{coupon?.name || t("page.auth.name")}</div>
                        <div className="date_text">
                            {dateFormFormat} - {dateToFormat}
                        </div>
                        <div className="nick_name" onClick={handleReceiveCoupon}>
                            {t("page.coupon.collect_coupon")} &gt;
                        </div>
                    </div>
                </div>
                <div className="code">
                    <div className="coupon_code">{t("page.coupon.coupon_code")}:</div>
                    <div className="number">{coupon?.code}</div>
                </div>
                <div className="content">
                    <Swiper slidesPerView={1}>
                        {coupon.imageUrl.length ? (
                            coupon.imageUrl.map((item, index) => (
                                <SwiperSlide key={index}>
                                    <img className="image" src={item} />
                                </SwiperSlide>
                            ))
                        ) : (
                            <SwiperSlide>
                                <img
                                    className="image"
                                    src={coupon.imageUrl[0] || BLANK_IMAGE_URL}
                                />
                            </SwiperSlide>
                        )}
                    </Swiper>
                    {coupon.guideline && (
                        <div className="guideline">{Parser(coupon.guideline)}</div>
                    )}
                </div>
                <div className="btn-main">
                    <StyledButton
                        size="large"
                        bg_color={themeColor}
                        type="submit"
                        onClick={handleToggleConfirm}
                    >
                        {t("page.coupon.use_coupon")}
                    </StyledButton>
                </div>
            </div>
        </StyledPreview>
    );
};

const StyledButton = styled(SharedButton)`
    margin-top: 16px;
`;
