import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import { ErrorMessage, FormikErrors, FormikTouched } from "formik";
import IntlTelInput from "react-intl-tel-input";
import "react-intl-tel-input/dist/main.css";

import {
    StyledSignUp,
    SharedSocialLine,
    SharedInput,
    SharedButton,
    IConPhone,
    StyledError,
} from "@components";
import { selectMerchant } from "@redux";
import { IPhone, IHandleBlur, IHandleChange, IExternalAuthState } from "@interfaces";
import { enumExternalAuthType } from "@configs";
import { ModuleSocialMedia } from "@modules";
interface IProps {
    values: IPhone;
    handleBlur: IHandleBlur;
    handleChange: IHandleChange;
    errors: FormikErrors<IPhone>;
    touched: FormikTouched<IPhone>;
    isSubmitting: boolean;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    setFieldTouched: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IPhone>>;
    setFieldValue: (
        field: string,
        value: any,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IPhone>>;
}

export const ComponentSignUpTel = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const {
        values,
        handleBlur,
        handleChange,
        errors,
        touched,
        isSubmitting,
        handleSubmit,
        setFieldTouched,
        setFieldValue,
    } = props;
    //page variable
    const merchant = useSelector(selectMerchant);
    const state: IExternalAuthState = {
        type: enumExternalAuthType.SIGNUP,
    };
    return (
        <StyledSignUp>
            <form onSubmit={handleSubmit}>
                <StyledContainer>
                    <div className="title">
                        {t("page.auth.sign_up_to")} {merchant.name}
                    </div>
                    <div className="choose">{t("page.auth.choose_sign_up_method")}</div>
                </StyledContainer>
                <ModuleSocialMedia state={state} />
                <SharedSocialLine />
                <SharedInput
                    prefixIcon={<IConPhone />}
                    placeholder={t("page.auth.tel")}
                    value={values.phoneNumber}
                    name="phoneNumber"
                    onBlur={handleBlur}
                    onChange={handleChange}
                    touched={touched.phoneNumber}
                    errors={errors.phoneNumber}
                />
                <IntlTelInput
                    defaultCountry="fr"
                    preferredCountries={["fr", "gb", "es", "be", "de"]}
                    fieldId="phoneNumber"
                    fieldName="phoneNumber"
                    onPhoneNumberChange={(...args) => {
                        console.log(args);
                        setFieldValue("phoneNumber", args[1]);
                        // console.log(this.formatPhoneNumberOutput(...args));
                    }}
                    onPhoneNumberBlur={() => {
                        setFieldTouched("phoneNumber", true);
                    }}
                    value={values.phoneNumber}
                    disabled={isSubmitting}
                />
                <ErrorMessage name="phoneNumber" component={StyledError} />
                <StyledButton
                    size="large"
                    bg_color={merchant.themeColor}
                    disabled={isSubmitting}
                    type="submit"
                >
                    {t("page.auth.sign_up")}
                </StyledButton>
            </form>
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 56px;
`;
