import styled from "styled-components";
import { useSelector } from "react-redux";

import { IVariant } from "@interfaces";
import { IConTick } from "@components";
import { selectMerchant } from "@redux";

export interface IProps {
    variants: IVariant[];
    variant: string;
    handleChangeVariant: (value: string) => void;
    handleToggleShowSelectType: () => void;
}

export const ComponentSelectType = (props: IProps) => {
    //page props
    const { variants, variant, handleChangeVariant, handleToggleShowSelectType } = props;
    const { themeColor } = useSelector(selectMerchant);
    return (
        <StyledSelectType>
            {variants.map((item) => {
                return (
                    <div
                        className="item"
                        onClick={() => {
                            handleChangeVariant(item.name);
                            handleToggleShowSelectType();
                        }}
                        key={item._id}
                    >
                        <div className="value">{item.name}</div>
                        {variant === item.name && (
                            <div className="tick">
                                <IConTick color={themeColor} />
                            </div>
                        )}
                    </div>
                );
            })}
        </StyledSelectType>
    );
};

const StyledSelectType = styled.div`
    margin-left: 12px;
    .item {
        padding: 18px 26px 18px 0;
        width: 100%;
        border-bottom: 1px solid #707070;
        font-family: "Segoe UI", "Roboto", sans-serif;
        font-style: normal;
        font-weight: 600;
        font-size: 18px;
        line-height: 1.8rem;
        color: #000000;
        display: flex;
        justify-content: space-between;
        align-items: center;
    }
    .value {
        font-family: "Segoe UI", "Roboto", sans-serif;
    }
`;
