import styled from "styled-components";

export const StyledHome = styled.div<{ mainColor: string }>`
    width: 100%;
    position: relative;
    top: -85px;
    .preview {
        width: 88%;
        position: relative;
        background-color: #fff;
        margin: 0 auto;
        padding: 16px;
        box-shadow: rgb(204 204 204) 0px 0px 3px 2px;
        padding: 12px;
        border-radius: 4px;
        .star {
            position: absolute;
            top: 12px;
            right: 16px;
            cursor: pointer;
        }
        .ant-avatar {
            position: absolute;
            top: 0;
            left: 50%;
            transform: translate(-50%, -50%);
        }
        .avatar_name {
            font-weight: bold;
            font-size: 20px;
            line-height: 27px;
            text-align: center;
            margin-top: 32px;
            font-family: Segoe UI;
        }
        .good {
            text-align: center;
            color: #fff;
            font-weight: bold;
            font-size: 8px;
            line-height: 11px;
            padding: 4px 8px;
            background: #816b00;
            display: inline-block;
            border-radius: 12px;
            position: relative;
            margin-top: 12px;
            top: -8px;
            span {
                display: inline-block;
                margin: 0 4px;
                font-size: 11px;
            }
        }
        .star_coupans {
            display: flex;
            align-items: center;
            justify-content: center;
            margin-bottom: 12px;
            .point {
                color: ${(p) => p.mainColor};
                font-family: bold;
                display: initial !important;
            }
            .coupon {
                font-family: bold;
                font-size: 16px;
                color: black;
                display: initial !important;
                margin-left: 4px;
            }
            .star_point {
                display: flex;
                flex-direction: column;
                justify-content: center;
                align-items: center;
                flex: 1;
                span {
                    display: block;
                }
