        }
    }
    .nav-bar {
        padding: 16px;
        background-color: #fff;
        display: flex;
        flex-wrap: wrap;
        justify-content: space-around;
        margin-top: 12px;
        .item {
            /* width: 25%; */
            text-align: center;
            padding: 15px 0;
            font-weight: normal;
            font-size: 16px;
            line-height: 21px;
            color: #989898;
            cursor: pointer;
            position: relative;
        }
        .under {
            color: #000000;
            font-style: normal;
            font-weight: bold;
            &::after {
                content: "";
                position: absolute;
                bottom: 0;
                left: 50%;
                width: 50%;
                height: 2px;
                background-color: ${(p) => p.mainColor};
                transform: translateX(-50%);
            }
        }
    }
`;
