import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import OtpInput from "react-otp-input";

import { SharedButton, StyledSignUp } from "@components";
import { selectMerchant } from "@redux";

export const ComponentSignUpOTP = () => {
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);

    return (
        <StyledSignUp>
            <StyledOTP numInputs={6} inputStyle={{
                 width: "3rem",
                 height: "3rem",
                 margin: "0 1rem",
                 fontSize: "2rem",
                 borderRadius: "4px",
                 border:" 1px solid rgba(0, 0, 0, 0.3)",
            }}/>
            <StyledButton size="large" bg_color={merchant.themeColor}>
                {t("page.sign_up.sign_up")}
            </StyledButton>
        </StyledSignUp>
    );
};

const StyledButton = styled(SharedButton)`
    margin-top: 56px;
`;

const StyledOTP = styled(OtpInput)`
    width: 3rem;
    height: 3rem;
    margin: 0 1rem;
    font-size: 2rem;
    border-radius: 4px;
    border: 1px solid rgba(0, 0, 0, 0.3);
`;
