import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";

import { ComponentHeaderAuth, ComponentSignInEmail } from "@components";
import { ComponentFaceBookButton, ComponentGoogleButton, ComponentLineButton } from "@components";
import { logout, selectMerchant } from "@redux";
import { useNotify } from "@utils";

export const PageSignInEmail = () => {
    //page hook
    const { t } = useTranslation();
    const dispatch = useDispatch();
    const { error } = useNotify();
    //page variable
    const merchant = useSelector(selectMerchant);

    const errorHandler = (errors: any) => {
        //handle Error
        if (errors.response) {
            const { data } = errors.response;
            if (data?.message) {
                error(data?.message);
            } else {
                error(t("page.sign_up.sign_up_failed"));
            }
            dispatch(logout());
        }
    };

    const socials: JSX.Element[] = [
        <ComponentFaceBookButton />,
        <ComponentGoogleButton />,
        <ComponentLineButton />,
    ];

    return (
        <StyledContainer color={merchant.themeColor}>
            <ComponentHeaderAuth />
            <ComponentSignInEmail socials={socials} />
            <div className="sign_in">
                {t("page.sign_up.already_have_an_account")} <span>{t("page.sign_up.sign_in")}</span>
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div<{ color: string }>`
    .sign_in {
        position: relative;
        top: -85px;
        text-align: end;
        margin: 36px 36px 0 0;
        font-style: normal;
        font-weight: bold;
        font-size: 16px;
        line-height: 21px;
        span {
            color: ${(p) => p.color};
            cursor: pointer;
        }
    }
`;
