import styled from "styled-components";
import { useSelector } from "react-redux";
import { useTranslation } from "react-i18next";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

import { IMemberTier } from "@interfaces";
import { selectMerchant } from "@redux";
import { ModuleMemberShipTier } from "@modules";

interface IProps {
    avatar: string;
    fullName: string;
    memberTier: IMemberTier | undefined;
}

export const ComponentTIerDetail = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const { avatar, fullName, memberTier } = props;
    //redux state
    const { membershipTiers, themeColor } = useSelector(selectMerchant);
    //page variable
    const membershipTier = membershipTiers?.find(
        (membershipTier) => membershipTier.points > (memberTier?.initPoint || 0)
    );

    return (
        <StyledTierDetail color={memberTier?.color || "#000"}>
            <div className="avatar">
                <div className="image">
                    <img src={avatar} />
                </div>
                <div className="info">
                    <div className="name">{fullName}</div>
                    <div className="rank">
                        <img src={memberTier?.iconUrl} />
                        <span> {memberTier?.tierName}</span>
                    </div>
                </div>
            </div>
            <div className="content">
                <div className="square">
                    <CircularProgressbar
                        value={66}
                        strokeWidth={8}
                        styles={buildStyles({
                            pathColor: themeColor,
                        })}
                    />
                    <div className="text">
                        <div className="point">
                            {memberTier?.initPoint || 0} {t("page.user_detail.of")}{" "}
                            {membershipTier?.points}
                        </div>
                        <div className="points">{t("page.user_detail.points")}</div>
                        <div className="to_level">
                            {t("page.user_detail.to_level", {
                                rank: membershipTier?.membershipName,
                            })}
                        </div>
                    </div>
                </div>
                <ModuleMemberShipTier />
            </div>
        </StyledTierDetail>
    );
};

const StyledTierDetail = styled.div<{ color: string }>`
    display: flex;
    flex-direction: column;
    min-height: 100vh;
    .avatar {
        padding: 32px 0;
        background-color: #fff;
        display: flex;
        align-items: center;
        .image {
            margin: 0 16px;
            img {
                border-radius: 50%;
            }
        }
        .info {
            .name {
                font-family: "Roboto", sans-serif;
                font-style: normal;
                font-weight: bold;
                font-size: 20px;
                color: #000000;
            }
            .rank {
                border-radius: 16px;
                padding: 4px 8px;
                font-family: Segoe UI, "Roboto", sans-serif;
                font-style: normal;
                font-weight: bold;
                font-size: 8px;
                background-color: ${(p) => p.color};
                width: fit-content;
                color: #fff;
                span {
                    display: inline-block;
                    margin-right: 4px;
                }
            }
        }
    }
    .content {
        flex: 1;
        background: #f7f7f8;
        .square {
            position: relative;
            height: fit-content;
            text-align: center;
            padding: 50px 0;
            img {
                width: 100%;
            }
            .text {
                position: absolute;
                top: 0;
                left: 0;
                display: flex;
                justify-content: center;
                align-items: center;
                flex-direction: column;
                width: 100%;
                height: 100%;
                .point {
                    font-family: "Roboto", sans-serif;
                    font-style: normal;
                    font-weight: bold;
                    font-size: 25px;
                    color: #000000;
                }
                .points {
                    margin-top: -4px;
                }
                .to_level {
                    font-family: "Roboto", sans-serif;
                    font-style: normal;
                    font-weight: normal;
                    font-size: 12px;
                    color: #9a9a9a;
                }
            }
        }
    }
    .CircularProgressbar {
        width: 60%;
    }
`;
