import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";

import {
    StyledSignUp,
} from "@components";
import { selectMerchant } from "@redux";
import OtpInput from "react-otp-input";

interface IProps {
    socials: JSX.Element[];
}

export const ComponentSignUpOTP = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const { socials } = props;
    //page variable
    const merchant = useSelector(selectMerchant);

    return (
        <StyledSignUp>
            <OtpInput
                numInputs={6}
                inputStyle={{
                  width: "3rem",
                  height: "3rem",
                  margin: "0 1rem",
                  fontSize: "2rem",
                  borderRadius: 4,
                  border: "1px solid rgba(0,0,0,0.3)"
                }}
              />
            <StyledButton size="large" bg_color={merchant.themeColor}>
                {t("page.sign_up.sign_up")}
            </StyledButton>

        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 56px;
`;
