import { useSelector } from "react-redux";
import { Avatar } from "antd";
import { useTranslation } from "react-i18next";

import {
    ComponentHeaderAuth,
    StyledHome,
    Coupon,
    Diamond,
    IconStarCircle,
    IconStarSub,
    ArrowRight,
} from "@components";
import { selectAuth, selectMerchant } from "@redux";
import { useState } from "react";

export const PageHome = () => {
    //page hook
    const { t } = useTranslation();
    //page redux
    const { userInfo } = useSelector(selectAuth);
    const { themeColor } = useSelector(selectMerchant);
    //page state
    const [under, setUnder] = useState();
    //page variable
    const navigation: Array<string> = [
        t("page.preview.use_point"),
        t("page.preview.privilege"),
        t("page.preview.coupon"),
        t("page.preview.record"),
    ];
    console.log("userInfo", userInfo);

    return (
        <>
            <ComponentHeaderAuth />
            <StyledHome mainColor={themeColor}>
                <div className="preview">
                    <div className="star">
                        <IconStarCircle color={themeColor} />
                    </div>
                    <Avatar alt="Avatar" size={62} src={userInfo?.avatar} />
                    <div className="avatar_name">
                        <div className="name">{userInfo?.fullName} </div>
                        <div className="good">
                            <Diamond /> <span>good</span> <ArrowRight />
                        </div>
                    </div>
                    <div className="star_coupans">
                        <div className="star_point">
                            <IconStarSub color={themeColor} />
                            <span className="point">1280</span>
                            <span className="desc">{t("page.home.points")}</span>
                        </div>
                        <div className="line"></div>
                        <div className="star_point">
                            <Coupon color={themeColor} />
                            <span className="point">
                                5 <span className="coupon">{t("page.home.coupon")}</span>
                            </span>
                            <span className="desc">{t("page.home.coupons")}</span>
                        </div>
                    </div>
                    <div className="desc_end">
                        {t("page.home.expire_in")} {t("page.home.month")}
                    </div>
                </div>
                <div className="nav-bar">
                    {navigation.map((item) => {
                        return (
                            <>
                                <div
                                    className={under === item ? `item under` : "item"}
                                    onClick={() => {
                                        onToggleUnder(item);
                                    }}
                                >
                                    {item}
                                </div>
                            </>
                        );
                    })}
                </div>
            </StyledHome>
        </>
    );
};
