import { Route, useHistory, useLocation } from "react-router";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import { IRoute } from "@interfaces";
import { LayoutApp } from "@layouts";
import {
    authRoutes,
    COUPON,
    enumExternalMethod,
    enumTypes,
    normalRoutes,
    PATH_404,
    PATH_HOME,
    PATH_SIGN_IN,
    PATH_SIGN_UP_EMAIL_FROM,
    PATH_SIGN_UP_FORM,
    PATH_SIGN_UP_SOCIAL_FROM,
    PATH_SIGN_UP_TEL_FROM,
} from "@configs";
import { getInfo, selectAuth, selectMerchant } from "@redux";

export const ComponentAppRoute = (props: IRoute) => {
    //page props
    const { path, exact } = props;
    //page hook
    const { pathname } = useLocation();
    const history = useHistory();
    const dispatch = useDispatch();
    //redux state
    const { auth, externalLogin, isSignUp, externalType } = useSelector(selectAuth);
    const { _id } = useSelector(selectMerchant);

    //page variable
    //WHAT: check user login
    const token = auth?.accessToken;
    const Component = props.component;
    const Layout = LayoutApp;

    //WHAT: check valid route
    useEffect(() => {
        //What: check merchantID
        if (!_id) {
            history.push(PATH_404);
        }

        //WHAT: check normal path
        const isNormalRoute =
            pathname === PATH_HOME
                ? true
                : normalRoutes.some((item: string) => {
                      if (item === PATH_HOME) {
                          return false;
                      }
                      return pathname.includes(item);
                  });

        //WHAT: check a uth path
        const isAuthRoute = authRoutes.some((item: string) => pathname.includes(item));
        if (token) {
            // const userPermissions = auth.userPermissions;
            // || (props.permission && !userPermissions?.includes(props.permission))
            if (externalType?.type === enumTypes.COUPON && externalType.id) {
                history.push(COUPON + "/" + externalType.id);
            } else if (isSignUp) {
                history.push(PATH_SIGN_UP_FORM);
            } else if (isAuthRoute) {
                history.push(PATH_HOME);
            }
        } else if (isNormalRoute) {
            history.push(PATH_SIGN_IN);
        }
    }, [pathname, auth]);

    useEffect(() => {
        if (token) {
            dispatch(getInfo());
        }
    }, []);

    return (
        <Route
            path={path}
            exact={exact}
            render={() => (
                <Layout>
                    <Component />
                </Layout>
            )}
        />
    );
};
