import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { useTranslation } from "react-i18next";

import { couponApi } from "@api";
import { CouponList } from "@components";
import { ICouponList } from "@interfaces";
import { setLoading } from "@redux";
import { enumStatus, enumTypes } from "@configs";
import { useNotify } from "@utils";

export const ModulesCoupon = () => {
    //page hooks
    const { t } = useTranslation();
    const dispatch = useDispatch();
    const { error, success } = useNotify();
    //page state
    const [coupons, setCoupons] = useState<ICouponList[]>([]);
    const [isShowPreview, setIsShowPreview] = useState<boolean>(false);

    useEffect(() => {
        (async () => {
            dispatch(setLoading(true));
            const res = await couponApi.getListCoupon();
            const data = res.data.docs as ICouponList[];
            setCoupons(data);
            dispatch(setLoading(false));
        })();
    }, []);

    const handleCoupon = async (id: string) => {
        try {
            dispatch(setLoading(true));
            const res = await couponApi.redeemedCoupon({
                objectId: id || "",
                status: enumStatus.ACTIVE,
                type: enumTypes.COUPON,
            });
            if (res.data.message) {
                const index = coupons.findIndex((item) => item.qrtable._id === id);
                coupons.splice(index, 1);
                setCoupons([...coupons]);
                success(res.data.message);
                handleToggleShowPreview();
            }
        } catch (err: any) {
            const { message } = err.response.data;
            if (message) {
                error(message);
            } else {
                error(t("message.received.fail"));
            }
        } finally {
            dispatch(setLoading(false));
        }
        //
    };

    const handleToggleShowPreview = () => {
        setIsShowPreview(!isShowPreview);
    };

    return (
        <>
            {coupons && (
                <CouponList
                    data={coupons}
                    handleCoupon={handleCoupon}
                    isShowPreview={isShowPreview}
                    handleToggleShowPreview={handleToggleShowPreview}
                />
            )}
        </>
    );
};
