import styled, { CSSProperties } from "styled-components";
import { useTranslation } from "react-i18next";
import { Avatar } from "antd";
import moment from "moment";

import { ArrowRight, IConCamera, IConUser } from "@components";
import { IUserSetting } from "@interfaces";
import { useSetting } from "@configs";

const styleAvatarUser: CSSProperties = {
    transform: "scale(2)",
};

interface IProps {
    handleUploadImage: (e: React.ChangeEventHandler<HTMLInputElement>) => void;
    values: IUserSetting;
    handleChangeTitle: (title: useSetting) => void;
}

export const ComponentUserSetting = (props: IProps) => {
    //page prop
    const { handleUploadImage, values, handleChangeTitle } = props;
    //page hooks
    const { t } = useTranslation();
    //page variable
    const dateFormat = moment(values?.dateOfBirth).format("DD/MM/YYYY");
    console.log(values?.dateOfBirth);
    return (
        <StyledUserSetting avatarCheck={!!values?.avatar}>
            <input type="file" hidden id="uploadImage" onChange={handleUploadImage} />
            <div className="image">
                <div className="box_image">
                    {values?.avatar ? (
                        <Avatar alt="Avatar" size={62} src={values?.avatar} />
                    ) : (
                        <IConUser className="ant-avatar" style={styleAvatarUser} />
                    )}
                    <label className="camera" htmlFor="uploadImage">
                        <IConCamera />
                    </label>
                </div>
            </div>
            <StyledCartUser>
                <div className="user_info">
                    <div className="title">{t("page.user_detail.name")}</div>
                    <div className="desc">{values?.fullName}</div>
                </div>
                <div className="arrowRight" onClick={() => handleChangeTitle(useSetting.FULL_NAME)}>
                    <ArrowRight />
                </div>
            </StyledCartUser>
            <StyledCartUser>
                <div className="user_info">
                    <div className="title">{t("page.user_detail.phone_number")}</div>
                    <div className="desc">{values?.phoneNumber}</div>
                </div>
                <div
                    className="arrowRight"
                    onClick={() => handleChangeTitle(useSetting.PHONE_NUMBER)}
                >
                    <ArrowRight />
                </div>
            </StyledCartUser>
            <StyledCartUser>
                <div className="user_info">
                    <div className="title">{t("page.user_detail.email")}</div>
                    <div className="desc">{values?.email}</div>
                </div>
                <div className="arrowRight" onClick={() => handleChangeTitle(useSetting.EMAIL)}>
                    <ArrowRight />
                </div>
            </StyledCartUser>
            <StyledCartUser>
                <div className="user_info">
                    <div className="title">{t("page.user_detail.date")}</div>
                    <div className="desc">{dateFormat}</div>
                </div>
                <div
                    className="arrowRight"
                    onClick={() => handleChangeTitle(useSetting.DATE_OF_BIRTH)}
                >
                    <ArrowRight />
                </div>
            </StyledCartUser>
            <div className="line">
                <StyledCartUser>
                    <div className="user_info">
                        <div className="desc">{t("page.user_detail.link_different")}</div>
                    </div>
                    <div className="arrowRight">
                        <ArrowRight />
                    </div>
                </StyledCartUser>
            </div>
            <div className="line_desc">{t("page.user_detail.request_link")}</div>
        </StyledUserSetting>
    );
};

const StyledUserSetting = styled.div<{ avatarCheck: boolean }>`
    .image {
        width: 100%;
        padding: 46px 0;
        background: #f7f7f8;
        /* background: #000; */
        text-align: center;
        img {
            border-radius: 50%;
        }
    }
    .box_image {
        position: relative;
        width: fit-content;
        margin: 0 auto;
    }
    .camera {
        position: absolute;
        bottom: 0;
        right: 0;
        cursor: pointer;
        ${(p) => {
            if (!p.avatarCheck)
                return `bottom: -15px;
                right: -15px;`;
        }}
    }
    .line {
        margin-top: 42px;
    }
    .line_desc {
        margin: 16px 12px;
        font-family: Segoe UI;
        font-style: normal;
        font-weight: normal;
        font-size: 14px;
        color: #707070;
    }
`;

const StyledCartUser = styled.div`
    padding: 9px 16px;
    background-color: #fff;
    border-bottom: 1px solid #eeeeee;
    display: flex;
    justify-content: space-between;
    align-items: center;
    .arrowRight {
        cursor: pointer;
    }
    &:last-child {
        border-bottom: initial;
    }
    .user_info {
        .title {
            font-family: Segoe UI;
            font-style: normal;
            font-weight: 600;
            font-size: 14px;
            color: #707070;
            margin-bottom: 4px;
        }
        .desc {
            font-family: Leelawadee UI;
            font-style: normal;
            font-weight: bold;
            font-size: 18px;
            color: #000000;
        }
    }
`;
