import { useDispatch } from "react-redux";
import { useEffect, useState } from "react";

import { setLoading } from "@redux";
import { historyApi } from "@api";
import { IBenefit, ICoupon, IFreePoint, IPagination, IReward } from "@interfaces";
import { enumTypes, INITIAL_LIST_PARAMS } from "@configs";
import { ComponentHistoryDetail, ComponentHistoryList } from "@components";

export const ModuleHistory = () => {
    //page hooks
    const dispatch = useDispatch();
    //page state
    const [histories, setHistories] = useState<(IBenefit | IReward | ICoupon)[]>([]);
    const [historiesDetail, setHistoryDetail] = useState<
        IBenefit | IReward | ICoupon | IFreePoint
    >();
    const [loadingPagination, setLoadingPagination] = useState<boolean>(false);
    const [param, setParam] = useState<IPagination>(INITIAL_LIST_PARAMS);

    useEffect(() => {
        (async () => {
            dispatch(setLoading(true));
            const res = await historyApi.getListHistory(param);
            const data = res.data.docs.map((item: any) => {
                if (item.type === enumTypes.REWARD || item.type === enumTypes.BENEFIT) {
                    return {
                        exchangedType: item.exchangedType,
                        idCart: item._id,
                        updatedAtHistory: item.updatedAt,
                        ...item.reward,
                    };
                } else if (item.type === enumTypes.COUPON) {
                    return {
                        exchangedType: item.exchangedType,
                        idCart: item._id,
                        updatedAtHistory: item.updatedAt,
                        ...item.qrtable,
                    };
                }
            }) as (IBenefit | IReward | ICoupon)[];
            setHistories(data);
            dispatch(setLoading(false));
        })();
    }, []);
    const handleClick = async (id: string, type: enumTypes) => {
        dispatch(setLoading(true));
        const res = await historyApi.getListHistoryDetailById(id);
        if (type === enumTypes.COUPON) {
            const data = res.data;
            setHistoryDetail(data.qrtable);
        } else if (type === enumTypes.REWARD || type === enumTypes.BENEFIT) {
            const data = res.data;
            setHistoryDetail({ ...data.reward, updatedAtHistory: data.updatedAt });
        }
        dispatch(setLoading(false));
    };

    const handleCloseDetail = () => {
        setHistoryDetail(undefined);
    };

    const handleLoadMore = async () => {
        const paramFake = {
            ...param,
            page: param.page + 1,
        };
        setParam(paramFake);
        setLoadingPagination(true);
        const res = await historyApi.getListHistory(paramFake);
        const data = res.data.docs.map((item: any) => {
            if (item.type === enumTypes.REWARD || item.type === enumTypes.BENEFIT) {
                return {
                    exchangedType: item.exchangedType,
                    idCart: item._id,
                    updatedAtHistory: item.updatedAt,
                    ...item.reward,
                };
            } else if (item.type === enumTypes.COUPON) {
                return {
                    exchangedType: item.exchangedType,
                    idCart: item._id,
                    updatedAtHistory: item.updatedAt,
                    ...item.qrtable,
                };
            }
        }) as (IBenefit | IReward | ICoupon)[];
        setHistories([...histories, ...data]);
        setLoadingPagination(false);
    };

    return (
        <>
            {histories.length !== 0 && (
                <>
                    {" "}
                    <ComponentHistoryList
                        handleClick={handleClick}
                        data={histories}
                        loadingPagination={loadingPagination}
                        handleLoadMore={handleLoadMore}
                    />{" "}
                    {historiesDetail && (
                        <ComponentHistoryDetail
                            data={historiesDetail}
                            handleCloseDetail={handleCloseDetail}
                        />
                    )}
                </>
            )}
        </>
    );
};
