import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import { FormikErrors } from "formik";
import IntlTelInput, { CountryData } from "react-intl-tel-input";
import "react-intl-tel-input/dist/main.css";

import { StyledSignUp, SharedSocialLine, SharedButton, StyledError } from "@components";
import { selectMerchant } from "@redux";
import { IPhone, IExternalAuthState } from "@interfaces";
import { enumExternalAuthType } from "@configs";
import { ModuleSocialMedia } from "@modules";
interface IProps {
    values: IPhone;
    errorPhone: string | undefined;
    isSubmitting: boolean;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    setFieldTouched: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IPhone>>;
    setFieldValue: (
        field: string,
        value: any,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IPhone>>;
    handleChangePhoneNumber: (
        isValid: boolean,
        value: string,
        selectedCountryData: CountryData,
        fullNumber: string,
        extension: string
    ) => void;
}

export const ComponentSignUpTel = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page props
    const {
        values,
        isSubmitting,
        handleSubmit,
        setFieldTouched,
        handleChangePhoneNumber,
        errorPhone,
    } = props;
    //page variable
    const merchant = useSelector(selectMerchant);
    const state: IExternalAuthState = {
        type: enumExternalAuthType.SIGNUP,
    };

    return (
        <StyledSignUp>
            <form onSubmit={handleSubmit}>
                <StyledContainer>
                    <div className="title">
                        {t("page.auth.sign_up_to")} {merchant.name}
                    </div>
                    <div className="choose">{t("page.auth.choose_sign_up_method")}</div>
                </StyledContainer>
                <ModuleSocialMedia state={state} />
                <SharedSocialLine />
                <IntlTelInput
                    defaultCountry="fr"
                    preferredCountries={["fr", "gb", "es", "be", "de"]}
                    fieldId="phoneNumber"
                    fieldName="phoneNumber"
                    onPhoneNumberChange={handleChangePhoneNumber}
                    onPhoneNumberBlur={() => {
                        setFieldTouched("phoneNumber", true);
                    }}
                    value={values.phoneNumber}
                    disabled={isSubmitting}
                />
                {errorPhone && <StyledError visible={true}>{errorPhone}</StyledError>}
                <StyledButton
                    size="large"
                    bg_color={merchant.themeColor}
                    disabled={isSubmitting}
                    type="submit"
                >
                    {t("page.auth.sign_up")}
                </StyledButton>
            </form>
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 56px;
`;
