import { Avatar } from "antd";
import { useTranslation } from "react-i18next";
import { CSSProperties } from "styled-components";
import styled from "styled-components";
import { useDispatch } from "react-redux";

import { Coupon, IconStarCircle, IconStarSub, IConUser } from "@components";
import { IInfo } from "@interfaces";
import { DEFAULT_EX_MONTH } from "@configs";
import { setDetailUser } from "@redux";

interface IProps {
    themeColor: string;
    userInfo: IInfo | null;
    navigation: Array<string>;
    under: string;
    handleToggleUnder: (value: string) => void;
    handleToggleQrCode: () => void;
    handleSwitchUserSetting: () => void;
    handleSwitchTier: () => void;
    handleCoupon: () => void;
}

const styleAvatarUser: CSSProperties = {
    transform: "scale(2)",
    left: "46%",
    top: "-8%",
};

export const ComponentHome = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    const dispatch = useDispatch();
    //page variable
    const {
        themeColor,
        userInfo,
        navigation,
        under,
        handleToggleUnder,
        handleToggleQrCode,
        handleSwitchUserSetting,
        handleSwitchTier,
        handleCoupon,
    } = props;

    const handleDetail = () => {
        dispatch(setDetailUser());
    };

    return (
        <>
            <div className="background"></div>
            <div className="preview">
                <div className="star" onClick={handleToggleQrCode}>
                    <IconStarCircle color={themeColor} />
                </div>
                {userInfo?.avatar ? (
                    <div onClick={handleSwitchUserSetting}>
                        <Avatar alt="Avatar" size={62} src={userInfo?.avatar} />
                    </div>
                ) : (
                    <IConUser
                        className="ant-avatar"
                        style={styleAvatarUser}
                        onClick={handleSwitchUserSetting}
                    />
                )}
                <div className="avatar_name">
                    <div className="name">{userInfo?.contact?.fullName} </div>
                    <StyledRank
                        onClick={handleDetail}
                        color={userInfo?.memberTier?.color || "#000"}
                    >
                        <img src={userInfo?.memberTier?.iconUrl || ""} />
                        <span>{userInfo?.memberTier?.tierName}</span> &gt;
                    </StyledRank>
                </div>
                <div className="star_coupans">
                    <div className="star_point">
                        <StyledIconStarSub color={themeColor} onClick={handleSwitchTier} />
                        <span className="point" onClick={handleSwitchTier}>
                            {userInfo?.totalPoints || 0}
                        </span>
                        <span className="desc" onClick={handleSwitchTier}>
                            {t("page.home.points")}
                        </span>
                    </div>
                    <div className="line"></div>
                    <div className="star_point">
                        <Coupon color={themeColor} onClick={handleCoupon} />
                        <span className="point" onClick={handleCoupon}>
                            {userInfo?.totalCoupons}{" "}
                            <span className="coupon">{t("page.home.coupon")}</span>
                        </span>
                        <span className="desc" onClick={handleCoupon}>
                            {t("page.home.coupons")}
                        </span>
                    </div>
                </div>
                <div className="desc_end">
                    {t("page.home.expire_in")} {t("page.home.month")}:{" "}
                    {userInfo?.pointsExpriedDateInMonth || DEFAULT_EX_MONTH}
                </div>
            </div>
            <div className="nav-bar">
                {navigation.map((item, index) => {
                    return (
                        <div
                            key={index}
                            className={under === item ? `item under` : "item"}
                            onClick={() => {
                                handleToggleUnder(item);
                            }}
                        >
                            {item}
                        </div>
                    );
                })}
            </div>
        </>
    );
};

const StyledRank = styled.div<{ color: string }>`
    font-family: "Segoe UI", "Roboto", sans-serif;
    font-style: normal;
    font-weight: bold;
    font-size: 8px;
    line-height: 11px;
    color: #ffffff;
    background-color: ${(p) => p.color};
    padding: 4px 8px;
    width: fit-content;
    margin: 0 auto;
    border-radius: 16px;
    img {
        width: 12px;
        margin-right: 4px;
    }
    span {
        display: inline-block;
        font-family: "Segoe UI", "Roboto", sans-serif;
        margin-right: 4px;
    }
    svg {
        position: relative;
        top: -1px;
    }
`;

const StyledIconStarSub = styled(IconStarSub)`
    cursor: pointer;
`;
