import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { useHistory, useLocation } from "react-router";

import { ComponentGmailButton, ComponentPhoneButton, ComponentSocialMedia } from "@components";
import { IExternalAuthState } from "@interfaces";
import { logout, selectMerchant } from "@redux";
import { useNotify } from "@utils";
import {
    enumExternalAuthType,
    enumExternalMethod,
    PATH_SIGN_IN_EMAIL,
    PATH_SIGN_IN_TEL,
    PATH_SIGN_UP_EMAIL,
    PATH_SIGN_UP_TEL,
} from "@configs";
import { ModuleFaceBook, ModuleGoogle, ModuleLine } from "@modules";

interface IProps {
    state: IExternalAuthState;
}

export const ModuleSocialMedia = (props: IProps) => {
    //page props
    const { state } = props;
    //page hook
    const { t } = useTranslation();
    const dispatch = useDispatch();
    const { error } = useNotify();
    const location = useLocation();
    const history = useHistory();
    //redux page
    const { signUpSettings } = useSelector(selectMerchant);
    //page state
    const [data, setData] = useState<JSX.Element[]>([]);
    //page variable
    const signUpMethods = signUpSettings?.signUpMethods;
    const socials: JSX.Element[] = [];
    const { pathname } = location;
    useEffect(() => {
        //
        if (state.type === enumExternalAuthType.SIGNUP) {
            if (signUpMethods?.includes(enumExternalMethod.LINE)) {
                socials.push(<ModuleLine state={state} />);
            }
            if (signUpMethods?.includes(enumExternalMethod.FACEBOOK)) {
                socials.push(<ModuleFaceBook errorHandler={errorHandler} state={state} />);
            }
            if (signUpMethods?.includes(enumExternalMethod.GOOGLE)) {
                socials.push(<ModuleGoogle errorHandler={errorHandler} state={state} />);
            }
            if (!(pathname === PATH_SIGN_UP_EMAIL)) {
                socials.push(<ComponentGmailButton onClick={handleSwitchEmail} />);
            }

            if (
                signUpMethods?.includes(enumExternalMethod.TEL) &&
                !(pathname === PATH_SIGN_UP_TEL)
            ) {
                socials.push(<ComponentPhoneButton onClick={handleSwitchPhone} />);
            }
        } else {
            socials.push(<ModuleLine state={state} />);
            socials.push(<ModuleFaceBook errorHandler={errorHandler} state={state} />);
            socials.push(<ModuleGoogle errorHandler={errorHandler} state={state} />);
            if (!(pathname === PATH_SIGN_IN_EMAIL)) {
                socials.push(<ComponentGmailButton onClick={handleSwitchEmail} />);
            }
            if (
                signUpMethods?.includes(enumExternalMethod.TEL) &&
                !(pathname === PATH_SIGN_IN_TEL)
            ) {
                socials.push(<ComponentPhoneButton onClick={handleSwitchPhone} />);
            }
        }
        setData(socials);
    }, [signUpMethods, pathname]);

    const handleSwitchPhone = () => {
        if (state.type === enumExternalAuthType.SIGNUP) {
            history.push(PATH_SIGN_UP_TEL);
        } else {
            history.push(PATH_SIGN_IN_TEL);
        }
    };

    const handleSwitchEmail = () => {
        if (state.type === enumExternalAuthType.SIGNUP) {
            history.push(PATH_SIGN_UP_EMAIL);
        } else {
            history.push(PATH_SIGN_IN_EMAIL);
        }
    };

    const errorHandler = (errors: any) => {
        //handle Error
        if (errors.response) {
            const { data, status } = errors.response;
            if (data?.message && status !== 500) {
                error(data?.message);
            } else {
                error(t("page.auth.sign_in_failed"));
            }
            dispatch(logout());
        }
    };

    return <ComponentSocialMedia socials={data} />;
};
