import queryString from "querystring";
import { useEffect } from "react";
import { useHistory } from "react-router";
import { useDispatch, useSelector } from "react-redux";

import {
    enumExternalAuthType,
    enumExternalMethod,
    LINE_AUTH_URL,
    PATH_SIGN_UP_SOCIAL_TEL,
} from "@configs";
import { lineLogin, logout, selectAuth, setExternalLogin } from "@redux";
import { LineApi } from "@api";
import { ILineLogin, ILineSignup, ILineState } from "@interfaces";
import { useNotify } from "@utils";
import { useTranslation } from "react-i18next";

export const PageLineLogin = () => {
    //page hook
    const dispatch = useDispatch();
    const { line } = useSelector(selectAuth);
    const history = useHistory();
    const { error } = useNotify();
    const { t } = useTranslation();
    //page variable
    const queryParams = queryString.parse(window.location.search.substring(1));

    const externalLine = async () => {
        const lineLoginApi = LineApi(LINE_AUTH_URL);
        try {
            if (line?.code) {
                const lineState = JSON.parse(line?.state) as ILineState;
                if (lineState.type === enumExternalAuthType.LOGIN) {
                    const lineAccessToken = await lineLoginApi.login(line?.code);
                    if (!lineAccessToken.data) {
                        dispatch(logout());
                    }
                    const lineLoginData: ILineLogin = {
                        token: lineAccessToken.data.access_token,
                        refreshToken: lineAccessToken.data.refresh_token,
                        tokenId: lineAccessToken.data.id_token,
                    };
                    const res = await dispatch(lineLogin(lineLoginData));
                    //WHAT: wrap function in create async thunk
                    //@ts-ignore
                    unwrapResult(res);

                    //@ts-ignore
                    if (!res.payload.accessToken) {
                        error(t("page.auth.email_password_correct"));
                    } else {
                        error(t("page.auth.login_successfully"));
                    }
                } else if (lineState.type === enumExternalAuthType.SIGNUP) {
                    const lineAccessToken = await lineLoginApi.login(line?.code);
                    if (!lineAccessToken.data) {
                        dispatch(logout());
                    }

                    const lineSignupData: ILineSignup = {
                        token: lineAccessToken.data.access_token,
                        refreshToken: lineAccessToken.data.refresh_token,
                        tokenId: lineAccessToken.data.id_token,
                        inviteId: lineState?.inviteId,
                    };

                    const res = await dispatch(lineSignup(lineSignupData));
                    //WHAT: wrap function in create async thunk
                    //@ts-ignore
                    unwrapResult(res);

                    //@ts-ignore
                    if (!res.payload.accessToken) {
                        dispatch(
                            openToast({
                                message: t("page.auth.email_password_correct"),
                                type: "error",
                                autoHideDuration: 2000,
                            })
                        );
                    } else {
                        dispatch(
                            openToast({
                                message: t("page.auth.login_successfully"),
                                type: "success",
                                autoHideDuration: 2000,
                            })
                        );
                    }
                } else {
                    dispatch(logout());
                }
            } else {
                dispatch(logout());
            }
        } catch (error) {
            errorHandler(error);
        }
    };

    const errorHandler = (error: any) => {
        //handle Error
        if (error.response) {
            const { status, data } = error.response;
            if (data?.message && status !== 500) {
                dispatch(
                    openToast({
                        message: data?.message,
                        type: "error",
                        autoHideDuration: 2000,
                    })
                );
            } else {
                dispatch(
                    openToast({
                        message: t("page.auth.login_failed"),
                        type: "error",
                        autoHideDuration: 2000,
                    })
                );
            }
        } else {
            dispatch(
                openToast({
                    message: t("page.auth.login_failed"),
                    type: "error",
                    autoHideDuration: 2000,
                })
            );
        }
        dispatch(logout());
    };

    useEffect(() => {
        if (queryParams.code) {
            if (queryParams.state) {
                dispatch(
                    setExternalLogin({
                        type: enumExternalMethod.LINE,
                        line: {
                            code: queryParams.code.toString(),
                            state: queryParams.state.toString(),
                        },
                    })
                );
            } else {
                dispatch(logout());
            }
        } else {
            dispatch(logout());
        }
        history.push(PATH_SIGN_UP_SOCIAL_TEL);
    }, [queryParams]);

    return <></>;
};
