import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import moment from "moment";

import { IConClose, SharedButton } from "@components";
import { selectAuth, selectMerchant } from "@redux";
import { IBenefit, ICoupon, IFreePoint, IReward } from "@interfaces";

interface IProps {
    handleCloseDetail: () => void;
    data: IBenefit | IReward | ICoupon | IFreePoint;
    handleConfirm: () => void;
    handleCalculatePoint?: () => number;
}

export const ComponentPopUpConfirm = (props: IProps) => {
    //page hooks
    const { t } = useTranslation();
    //redux states
    const { themeColor } = useSelector(selectMerchant);
    const { userInfo } = useSelector(selectAuth);
    //page redux
    const { handleCloseDetail, data, handleConfirm, handleCalculatePoint } = props;
    //page variable
    const dateFormat = moment(new Date(Date.now())).format("DD/MM/YYYY h:mm:ss a");

    return (
        <StyledContainerHistoryDetail>
            <div className="content">
                <div className="close" onClick={handleCloseDetail}>
                    <IConClose />
                </div>
                <div className="confirmation">{t("page.history.confirmation")}</div>
                <div className="title">{t("page.history.name")}</div>
                <div className="desc">{userInfo?.fullName}</div>
                <div className="title">{t("page.history.item")}</div>
                <div className="desc">
                    {data.name}{" "}
                    {handleCalculatePoint && (
                        <>
                            , {handleCalculatePoint()}{" "}
                            {handleCalculatePoint() > 1
                                ? t("page.history.points")
                                : t("page.history.point")}
                        </>
                    )}
                </div>
                {data?.code && (
                    <>
                        {" "}
                        <div className="title">{t("page.history.ticket")}</div>{" "}
                        <div className="desc">{data?.code}</div>
                    </>
                )}
                <div className="title">{t("page.history.date_time")}:</div>
                <div className="desc">{dateFormat}</div>
                <div className="title desConfirm">{t("page.history.desConfirm")}</div>
                <StyledButton
                    size="large"
                    bg_color={themeColor}
                    type="submit"
                    onClick={handleConfirm}
                >
                    {t("page.history.confirm")}
                </StyledButton>
            </div>
        </StyledContainerHistoryDetail>
    );
};

const StyledContainerHistoryDetail = styled.div`
    height: 100vh;
    width: 100%;
    position: fixed;
    top: 0;
    display: flex;
    /* text-align: center; */
    align-items: center;
    justify-content: center;
    max-width: 480px;
    background: rgba(0, 0, 0, 0.5);
    z-index: 10;
    .content {
        width: 90%;
        padding: 28px;
        background-color: #fff;
        position: relative;
        border-radius: 8px;
        max-height: 90%;
        overflow: scroll;
        .close {
            position: absolute;
            top: 20px;
            right: 20px;
            cursor: pointer;
        }
    }
    .confirmation {
        font-family: "Segoe UI", "Roboto", sans-serif;
        font-weight: 600;
        font-size: 18px;
        line-height: 24px;
        color: #000000;
        padding-bottom: 14px;
        border-bottom: 1px solid rgba(115, 115, 115, 0.31);
    }
    .title {
        margin: 24px 0 4px 0;
        font-weight: bold;
        font-size: 14px;
        line-height: 19px;
        color: #989898;
        font-family: "Leelawadee UI", "Roboto", sans-serif;
    }
    .desc {
        font-weight: 600;
        font-size: 16px;
        color: #000000;
        font-family: "Segoe UI", "Roboto", sans-serif;
    }
    .desConfirm {
        width: 100%;
        margin-top: 24px;
        text-align: center;
    }
`;

const StyledButton = styled(SharedButton)`
    width: 100%;
    margin: 46px auto 20px auto;
`;
