import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import { useHistory } from "react-router";

import { ComponentHeaderAuth, StyledContainerSignIn } from "@components";
import { selectMerchant } from "@redux";
import { ModuleEmailSignIn } from "@modules";
import { enumExternalMethod, PATH_RESET_PASSWORD_EMAIL, PATH_SIGN_UP_EMAIL } from "@configs";

export const PageSignInEmail = () => {
    //page hook
    const { t } = useTranslation();
    const history = useHistory();
    //page variable
    const merchant = useSelector(selectMerchant);

    const handleSignUp = () => {
        if (merchant?.signUpSettings?.signUpMethods.includes(enumExternalMethod.EMAIL)) {
            history.push(PATH_SIGN_UP_EMAIL);
        }
    };

    const handleForgotPassword = () => {
        history.push(PATH_RESET_PASSWORD_EMAIL);
    };
    return (
        <StyledContainerSignIn color={merchant.themeColor}>
            <ComponentHeaderAuth />
            <ModuleEmailSignIn handleForgotPassword={handleForgotPassword} />
            <div className="sign_in">
                {t("page.auth.already_have_an_account")}{" "}
                <span onClick={handleSignUp}>{t("page.auth.sign_up")}</span>
            </div>
        </StyledContainerSignIn>
    );
};
