import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import { Checkbox } from "antd";
import IntlTelInput, { CountryData } from "react-intl-tel-input";
import { FormikErrors } from "formik";

import { StyledSignUp, SharedSocialLine, SharedInput, SharedButton, IConPhone } from "@components";
import { selectMerchant } from "@redux";
import { ModuleSocialMedia } from "@modules";
import { enumExternalAuthType } from "@configs";
import { IExternalAuthState, IPhone } from "@interfaces";

interface IProps {
    values: IPhone;
    errorPhone: string | undefined;
    isSubmitting: boolean;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    setFieldTouched: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IPhone>>;
    setFieldValue: (
        field: string,
        value: any,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IPhone>>;
    handleChangePhoneNumber: (
        isValid: boolean,
        value: string,
        selectedCountryData: CountryData,
        fullNumber: string,
        extension: string
    ) => void;
}

export const ComponentSignInTel = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);
    const state: IExternalAuthState = {
        type: enumExternalAuthType.LOGIN,
    };

    //page props
    const {
        values,
        isSubmitting,
        handleSubmit,
        setFieldTouched,
        handleChangePhoneNumber,
        errorPhone,
    } = props;

    return (
        <StyledSignUp>
            <form onSubmit={handleSubmit}>
                <StyledContainer>
                    <div className="title">
                        {t("page.auth.sign_in_to")} {merchant.name}
                    </div>
                    <div className="choose">{t("page.auth.choose_sign_up_method")}</div>
                    <ModuleSocialMedia state={state} />
                    <SharedSocialLine />
                    <IntlTelInput
                        defaultCountry="fr"
                        preferredCountries={["fr", "gb", "es", "be", "de"]}
                        fieldId="phoneNumber"
                        fieldName="phoneNumber"
                        onPhoneNumberChange={handleChangePhoneNumber}
                        onPhoneNumberBlur={() => {
                            setFieldTouched("phoneNumber", true);
                        }}
                        value={values.phoneNumber}
                        disabled={isSubmitting}
                    />
                    {errorPhone && <StyledError visible={true}>{errorPhone}</StyledError>}
                    <StyledButton size="large" bg_color={merchant.themeColor}>
                        {t("page.auth.sign_in")}
                    </StyledButton>
                    <div className="remember">
                        <Checkbox>{t("page.auth.remember_me")}</Checkbox>
                    </div>
                </StyledContainer>
            </form>
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
    .remember {
        text-align: start;
        font-weight: bold;
        font-size: 16px;
        line-height: 21px;
        margin-top: 16px;
        color: #646464;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 24px;
`;
