import { useSelector } from "react-redux";
import { useTranslation } from "react-i18next";
import styled from "styled-components";
import QRCode from "react-qr-code";

import { IConRoom, SharedButton } from "@components";
import { selectAuth, selectMerchant } from "@redux";

interface IProps {
    handleToggleQrCode: () => void;
}

export const ComponentQRcode = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //redux states
    const { userInfo } = useSelector(selectAuth);
    const { themeColor } = useSelector(selectMerchant);
    //props
    const { handleToggleQrCode } = props;

    return (
        <StyledContainer>
            {" "}
            <div className="room" onClick={handleToggleQrCode}>
                <IConRoom />
            </div>
            <div className="content">
                <div className="my_qrCode">{t("page.qrCode.qrCode")}</div>
                <QRCode value={userInfo?._id || ""} size={200} />
                <div className="merchant_id">
                    <p className="title">{t("page.qrCode.member_id")}</p>
                    <p className="code">{userInfo?._id}</p>
                </div>
                <StyledButton
                    size="large"
                    bg_color={themeColor}
                    type="submit"
                    onClick={handleToggleQrCode}
                >
                    {t("page.qrCode.done")}
                </StyledButton>
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    width: 100%;
    /* position: fixed; */
    top: 103px;
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: 480px;
    background: #fff;
    padding: 70px 0;
    border-radius: 16px;
    z-index: 100;
    .content {
        width: 70%;
        margin: 0 auto;
        text-align: center;
    }
    .room {
        position: absolute;
        top: 24px;
        right: 24px;
        cursor: pointer;
    }
    .my_qrCode {
        font-weight: bold;
        font-size: 20px;
        line-height: 1.5rem;
        text-align: center;
        color: #000000;
        margin-bottom: 36px;
        font-family: "Segoe UI", "Roboto", sans-serif;
    }
    .merchant_id {
        margin-top: 36px;
        padding: 16px 24px;
        background: #f2f2f2;
        p {
            margin: initial;
        }
        .title {
            font-family: "Segoe UI", "Roboto", sans-serif;
        }
        .code {
            color: #000000;
            font-weight: bold;
            font-size: 20px;
            margin-top: 4px;
            word-break: break-word;
            line-height: 1.5rem;
            font-family: "Segoe UI", "Roboto", sans-serif;
        }
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 12px;
`;
