import Parser from "html-react-parser";

import { BLANK_IMAGE_URL } from "@configs";
import { IReward } from "@interfaces";
import styled from "styled-components";

interface IProps {
    data: IReward;
}

export const RewardItem = (props: IProps) => {
    //page props
    const { data } = props;

    return (
        <StyledContainer slot="container-end">
            <img src={data.imageUrl[0] || BLANK_IMAGE_URL} />
            <div className="desc">{Parser(data.description)}</div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    width: 209px;
    background-color: #fff;
    margin: 0 24px 28px 0;
    border-radius: 12px;

    img {
        width: 100%;
        height: 209px;
        object-fit: contain;
    }
    .desc {
        font-weight: normal;
        font-size: 15px;
        line-height: 1.5rem;
        margin-top: 8px;
        color: #000000;
        display: -webkit-box;
        -webkit-line-clamp: 3;
        -webkit-box-orient: vertical;
        overflow: hidden;
        padding: 8px 12px 18px 12px;
    }
`;
