import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { useHistory } from "react-router";

import { ComponentHeaderAuth, ComponentSignUpOTP } from "@components";
import { selectAuth, setAuth, setLoading } from "@redux";
import { enumExternalMethod, enumValidation, PATH_SIGN_UP, TIME_RESEND } from "@configs";
import { authApi } from "@api";
import { useNotify } from "@utils";
import { IAuth } from "@interfaces";
import { PageUserDetail } from "@pages";

export const PageSignUpOTP = () => {
    //page hook
    const history = useHistory();
    const { error, success } = useNotify();
    const dispatch = useDispatch();
    //page state
    const [otp, setOtp] = useState<string>("");
    const [timeResend, setTimeResend] = useState<number>(TIME_RESEND);
    //redux state
    const { externalLogin, detailUser } = useSelector(selectAuth);
    //page variable
    const phoneNumber = window.sessionStorage.getItem("phoneNumber") || "";
    const remember = window.sessionStorage.getItem("remember");

    useEffect(() => {
        if (externalLogin !== enumExternalMethod.TEL) {
            history.push(PATH_SIGN_UP);
        }
    }, []);

    const handleChangeOtp = (otp: string) => {
        setOtp(otp);
    };

    const handleVerifyOtp = async () => {
        if (otp.length < enumValidation.LENTH_OTP) return;
        try {
            dispatch(setLoading(true));
            const res = await authApi.verifyPhone({
                phoneNumber,
                code: otp,
            });
            const data = res.data.token as IAuth;
            const message = res.data.message;
            if (remember) {
                dispatch(setAuth(data));
            } else {
                dispatch(setAuth({ ...data, refreshToken: null }));
            }
            success(message);
        } catch (errors: any) {
            const { message } = errors.response.data;
            error(message);
        } finally {
            dispatch(setLoading(false));
        }
    };

    const handleResend = async () => {
        if (timeResend !== TIME_RESEND) return;
        try {
            dispatch(setLoading(true));
            const res = await authApi.resendPhone({
                phoneNumber,
            });
            if (res.data.message) {
                success(res.data.message);
                let currentTime = timeResend;
                const id = setInterval(() => {
                    currentTime = currentTime - 1;
                    setTimeResend(() => currentTime);
                    if (currentTime === 0) {
                        setTimeResend(TIME_RESEND);
                        clearInterval(id);
                    }
                }, 1000);
            }
        } catch (errors: any) {
            const { message } = errors.response.data;
            error(message);
        } finally {
            dispatch(setLoading(false));
        }
    };

    return (
        <>
            {!detailUser ? (
                <>
                    <ComponentHeaderAuth />
                    <ComponentSignUpOTP
                        handleVerifyOtp={handleVerifyOtp}
                        handleChangeOtp={handleChangeOtp}
                        handleResend={handleResend}
                        otp={otp}
                        timeResend={timeResend}
                    />
                </>
            ) : (
                <PageUserDetail />
            )}
        </>
    );
};
