import { FormikErrors, FormikTouched } from "formik";
import styled from "styled-components";
import { useTranslation } from "react-i18next";
import { DatePicker } from "antd";
import moment from "moment";

import { IHandleBlur, IUserSetting, IHandleChange } from "@interfaces";
import { SharedButton, SharedInput, SharedIntlTelInput } from "@components";
import { enumUseSetting, enumValidation } from "@configs";

interface IProps {
    values: IUserSetting;
    handleBlur: IHandleBlur;
    handleChange: IHandleChange;
    errors: FormikErrors<IUserSetting>;
    touched: FormikTouched<IUserSetting>;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    isSubmitting: boolean;
    title: enumUseSetting;
    setFieldTouched: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IUserSetting>>;
    setFieldValue: (
        field: string,
        value: any,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IUserSetting>>;
    setError: React.Dispatch<React.SetStateAction<string | undefined>>;
    errorPhone: string;
    handleChangeDate: (date: moment.Moment | null, dateString: string) => void;
}

export const ComponentUserSettingSave = (props: IProps) => {
    //page hooks
    const { t } = useTranslation();
    //page props
    const {
        values,
        handleBlur,
        errors,
        touched,
        handleSubmit,
        isSubmitting,
        title,
        setFieldTouched,
        errorPhone,
        handleChangeDate,
        setFieldValue,
        setError,
    } = props;

    console.log(errors);

    const render = () => {
        switch (title) {
            case enumUseSetting.FULL_NAME:
                return t("page.user_detail.name");
                break;
            case enumUseSetting.PHONE_NUMBER:
                return t("page.user_detail.phone_number");
                break;
            case enumUseSetting.EMAIL:
                return t("page.user_detail.email");
                break;
            case enumUseSetting.DATE_OF_BIRTH:
                return t("page.user_detail.date_of_birth");
                break;
            default:
                return <></>;
                break;
        }
    };

    const renderName = () => {
        switch (title) {
            case enumUseSetting.FULL_NAME:
                return enumUseSetting.FULL_NAME;
                break;
            case enumUseSetting.PHONE_NUMBER:
                return enumUseSetting.PHONE_NUMBER;
                break;
            case enumUseSetting.EMAIL:
                return enumUseSetting.EMAIL;
                break;
            default:
                return "";
                break;
        }
    };

    const disableDate = (current: moment.Moment | null | undefined) => {
        const maxDate = moment(new Date(Date.now()));
        if (current) {
            return current.isAfter(maxDate);
        }
        return false;
    };

    return (
        <StyledSettingSave>
            {title === enumUseSetting.FULL_NAME && (
                <div className="length">
                    {values[title]?.length}/{enumValidation.MAX_LENGTH_INPUT}
                </div>
            )}
            <form onSubmit={handleSubmit}>
                <div className="title">{render()}</div>
                {title !== enumUseSetting.DATE_OF_BIRTH &&
                    (title === enumUseSetting.PHONE_NUMBER ? (
                        <SharedIntlTelInput
                            setFieldTouched={setFieldTouched}
                            setFieldValue={setFieldValue}
                            value={values[title] || ""}
                            errorPhone={errorPhone}
                            setError={setError}
                        />
                    ) : (
                        <StyledSharedInput
                            name={renderName()}
                            value={values[title]}
                            onBlur={handleBlur}
                            onChange={(e: React.ChangeEvent<any>) => {
                                const value: string = e.target.value;
                                if (value[value.length - 2] && value.trim()) {
                                    setFieldValue(renderName(), value);
                                } else {
                                    setFieldValue(renderName(), value.trim());
                                }
                            }}
                            errors={errors[title]}
                            touched={touched[title]}
                        />
                    ))}

                {title === enumUseSetting.DATE_OF_BIRTH && (
                    <DatePicker
                        value={moment(values[title] || undefined)}
                        onChange={handleChangeDate}
                        disabledDate={disableDate}
                    />
                )}

                <StyledSharedButton
                    size="large"
                    bg_color="#05C755"
                    disabled={isSubmitting}
                    type="submit"
                >
                    {t("page.auth.save")}
                </StyledSharedButton>
            </form>
        </StyledSettingSave>
    );
};

const StyledSharedInput = styled(SharedInput)`
    width: 90%;
    margin: 0 auto;
`;

const StyledSettingSave = styled.div`
    text-align: center;
    padding-top: 24px;
    .title {
        font-family: "Segoe UI", "Roboto", sans-serif;
        font-style: normal;
        font-weight: 600;
        font-size: 14px;
        color: #707070;
        margin-bottom: 12px;
    }
    .allow-dropdown.intl-tel-input {
        width: 90%;
    }
    .intl-tel-input.allow-dropdown input[type="text"],
    .intl-tel-input.allow-dropdown input[type="tel"] {
        height: 48px !important;
        outline: none;
        border: none;
        background-color: #f7f7f8;
        width: 100% !important;
        font-size: 16px;
        color: black;
        box-shadow: none !important;
        font-weight: 500;
    }
    .ant-calendar-picker {
        width: 90%;
    }
    .ant-calendar-picker-input.ant-input {
        height: 48px;
        font-size: 16px;
    }
    .ant-input:focus {
        border-color: initial !important;
        box-shadow: initial;
    }
    .custom-error {
        margin-left: 5%;
    }
    .selected-dial-code {
        padding-left: 0 !important;
    }
    .selected-flag {
        display: flex !important;
    }
    .intl-tel-input .country-list {
        width: 100%;
    }
    .intl-tel-input input {
        border-radius: 6px;
    }
    input {
        font-family: "Leelawadee UI", "Roboto", sans-serif;
    }
    .length {
        position: absolute;
        right: 5%;
        top: 24px;
        font-family: "Segoe UI", "Roboto", sans-serif;
    }
`;

const StyledSharedButton = styled(SharedButton)`
    width: 90%;
    position: absolute;
    bottom: 48px;
    left: 5%;
`;
