import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import OtpInput from "react-otp-input";

import { IConReSend, SharedButton, StyledSignUp } from "@components";
import { selectMerchant } from "@redux";
import { TIME_RESEND } from "@configs";
import { useWindowDimensions } from "@utils";

interface IProps {
    handleChangeOtp: (values: string) => void;
    handleVerifyOtp: () => void;
    otp: string;
    handleResend: () => void;
    timeResend: number;
}

export const ComponentSignUpOTP = (props: IProps) => {
    //page props
    const { handleChangeOtp, otp, handleVerifyOtp, handleResend, timeResend } = props;
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);
    const { width } = useWindowDimensions();

    return (
        <StyledSignUp>
            <StyledContainer>
                <div className="title">{t("page.auth.please_enter_OTP")}</div>
                <OtpInput
                    numInputs={6}
                    inputStyle={{
                        width: width < 380 ? "2.5rem" : "3rem",
                        height: "3rem",
                        margin: "1rem 0.5rem",
                        fontSize: "2rem",
                        borderRadius: "4px",
                        border: " 1px solid rgba(0, 0, 0, 0.3)",
                    }}
                    containerStyle={{
                        justifyContent: "center",
                        flexWrap: "wrap",
                        margin: "5rem 0 2rem 0",
                    }}
                    isInputNum
                    onChange={handleChangeOtp}
                    value={otp}
                />
                <div className="resend">
                    {timeResend === TIME_RESEND ? <IConReSend /> : timeResend}
                    <span onClick={handleResend}>{t("page.auth.resend_OTP")}</span>
                </div>
                <StyledButton size="large" bg_color={merchant.themeColor} onClick={handleVerifyOtp}>
                    {t("page.auth.submit")}
                </StyledButton>
            </StyledContainer>
        </StyledSignUp>
    );
};

const StyledButton = styled(SharedButton)`
    margin-top: 16px;
`;

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .resend {
        cursor: pointer;
        span {
            display: inline-block;
            margin-left: 8px;
            font-style: normal;
            font-weight: normal;
            font-size: 16px;
            line-height: 21px;
            color: #676767;
            position: relative;
            top: 2px;
        }
    }
`;
